INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (79657, 'Dronedarone', 'Darunavir', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of dronedarone, which is primarily metabolized by the isoenzyme. Because dronedarone is associated with concentration-dependent prolongation of the QT interval, increased levels may potentiate the risk of ventricular arrhythmias such as torsade de pointes and sudden death.', 'DDInter', 'The use of dronedarone in combination with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, voriconazole, nefazodone, cyclosporine, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics is considered contraindicated. Patients currently receiving these agents should stop taking them before starting treatment with dronedarone. Some authorities consider concomitant administration of dronedarone and itraconazole to be contraindicated during and for 2 weeks after treatment with itraconazole.', 'Metabolism', 'The use of dronedarone in combination with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, voriconazole, nefazodone, cyclosporine, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics is considered contraindicated.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/79758/', '[1] "Product Information. Sporanox (itraconazole)." Janssen Pharmaceutica, Titusville, NJ.[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Multaq (dronedarone)." sanofi-aventis , Bridgewater, NJ.[4] "Product Information. Multaq (dronedarone)." sanofi-aventis , Bridgewater, NJ.[5] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendatio