INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (79794, 'Darunavir', 'Naldemedine', 'Moderate', 'Coadministration with moderate inhibitors of CYP450 3A4 and/or moderate or potent inhibitors of P-glycoprotein (P-gp) may increase the plasma concentrations of naldemedine, which is primarily metabolized by CYP450 3A4 to nor-naldemedine and to a minor extent by UGT1A3 to naldemedine 3-G. Both metabolites have demonstrated antagonistic activity for opioid receptors, but with less potency than the parent drug.', 'DDInter', 'Caution is advised during concomitant use of naldemedine with moderate inhibitors of CYP450 3A4 and/or moderate or potent inhibitors of P-glycoprotein (P-gp). Patients should be closely monitored for potential opioid withdrawal symptoms as well as other adverse effects of naldemedine.', 'Metabolism', 'Caution is advised during concomitant use of naldemedine with moderate inhibitors of CYP450 3A4 and/or moderate or potent inhibitors of P-glycoprotein (P-gp).', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/79895/', '[1] "Product Information. Symproic (naldemedine)." Shionogi USA Inc, Florham Park, NJ.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Symproic (naldemedine)." Shionogi USA Inc, Florham Park, NJ.', 'Lamivudine, Paritaprevir, Tenofovir alafenamide, Maraviroc, Cabotegravir, Valaciclovir, Zanamivir, Rilpivirine, Dolutegravir, Oseltamivir, Acyclovir, More', 'Alvimopan, Sorbitol, Tegaserod, Bisacodyl, Lactulose, Methylnaltrexone, Lactitol, Sodium phosphate, monobasic, Magnesium oxide, Sodium sulfate, Prucalopride, More', 1767369485);
