INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (79799, 'Darunavir', 'Nevirapine', 'Moderate', 'Coadministration of darunavir-cobicistat with nevirapine may decrease the plasma concentrations of darunavir and/or cobicistat and increase the plasma concentrations of nevirapine. Reduced darunavir plasma levels may lead to diminished virologic response and possible resistance to darunavir. Increased nevirapine plasma concentrations may increase the risk of nevirapine-associated toxicity, including hepatotoxicity, fatal and non-fatal skin reactions (e.g., Stevens Johnson syndrome), and hypersensitivity reactions. The mechanism is nevirapine induction of the CYP450 3A4 isoenzyme and inhibition of CYP450 3A4 activity by darunavir and cobicistat.', 'DDInter', 'The concomitant use of darunavir-cobicistat with nevirapine is not recommended. As an alternative, darunavir boosted by low-dose ritonavir may be considered for use in combination with nevirapine.', 'Metabolism', 'The concomitant use of darunavir-cobicistat with nevirapine is not recommended.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/79900/', '[1] "Product Information. Viramune (nevirapine)." Boehringer-Ingelheim, Ridgefield, CT.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Prezista (darunavir)." Ortho Biotech Inc, Bridgewater, NJ.[4] Cerner Multum, Inc. "Australian Product Information." O 0[5] "Product Information. Prezcobix (cobicistat-darunavir)." Janssen Pharmaceuticals, Titusville, NJ.[6] "Product Information. Prezista (darunavir)." Ortho Biotech Inc, Bridgewater, NJ.', 'Lamivudine, Cabotegravir, Amprenavir, Zanamivir, Valaciclovir, Velpatasvir, Oseltamivir, Cobicistat, Acyclovir, Stavudine, Famciclovir, More', 'Teno