INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (79835, 'Darunavir', 'Pravastatin', 'Moderate', 'Coadministration with darunavir/ritonavir may increase the plasma concentrations of pravastatin. The mechanism of interaction has not been described.', 'DDInter', 'Because high levels of HMG-CoA reductase inhibitory activity in plasma is associated with an increased risk of musculoskeletal toxicity including severe myopathy and rhabdomyolysis, pravastatin should be initiated at the lowest possible dosage when prescribed with darunavir/ritonavir. The dosage may be increased gradually based on clinical response. All patients treated with HMG-CoA reductase inhibitors should be advised to promptly report any unexplained muscle pain, tenderness, or weakness, particularly if accompanied by malaise or fever. Therapy should be discontinued if creatine kinase is markedly elevated in the absence of strenuous exercise or if myopathy is otherwise suspected or diagnosed.', 'Others', 'Because high levels of HMG-CoA reductase inhibitory activity in plasma is associated with an increased risk of musculoskeletal toxicity including severe myopathy and rhabdomyolysis, pravastatin should be initiated at the lowest possible dosage when prescribed with darunavir/ritonavir.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/79936/', '[1] "Product Information. Pravachol (pravastatin)." Bristol-Myers Squibb, Princeton, NJ.[2] "Product Information. Prezista (darunavir)." Ortho Biotech Inc, Bridgewater, NJ.[3] "Product Information. Prezista (darunavir)." Ortho Biotech Inc, Bridgewater, NJ.', 'Gemfibrozil, Fenofibric acid, Pitavastatin, Evinacumab, Evolocumab, Probucol, Dextrothyroxine, Cholestyramine, Ezetimibe, Clofibrate, Colestipol, More', 'Tenofovir alafenamide, Cabotegravir, Maraviroc, Amprenavir, Zanamivir, Lenacapavir, Telaprevir, Velpatasvir, Rilpivirine, Dolutegravir, Oseltamivir, More', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (79836, 'Darunavir', 'Praziquantel', 'Moderate', 'Coadministration with darunavir may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by darunavir.', 'DDInter', 'Caution is advised if darunavir must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever darunavir is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if darunavir must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/79937/', '[1] "Product Information. Prezista (darunavir)." Ortho Biotech Inc, Bridgewater, NJ.[2] "Product Information. Prezista (darunavir)." Ortho Biotech Inc, Bridgewater, NJ.[3] Schubert W, Eriksson U, Edgar B, Cullberg G, Hedner T "Flavonoids in grapefruit juice inhibit the in vitro hepatic metabolism of 17B-estradiol." Eur J Drug Metab Pharmacokinet 20 (1995): 219-24[4] Weber A, Jager R, Borner A, et al. "Can grapefruit juice influence ethinyl estradiol bioavailability?" Contraception 53 (1996): 41-7', 'Triclabendazole, Oxamniquine', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Amprenavir, Simeprevir, Valaciclovir, Zanamivir, Cabotegravir, Telaprevir, More', 1767369485);
