INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (90079, 'Donepezil', 'Choline salicylate', 'Minor', 'Donepezil may increase gastric acid secretion by increasing cholinergic activity. The risk for gastrointestinal bleeding is, therefore, theoretically increased in patients receiving donepezil in combination with a nonsteroidal anti-inflammatory agent. Caution is recommended when this combination is used.', 'DDInter', '-', 'Synergism', '-', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/90181/', '[1] "Product Information. Aricept (donepezil)." Pfizer US Pharmaceuticals, New York, NY.', 'Rivastigmine, Galantamine, Tacrine, Memantine, Ginkgo biloba, Aducanumab', 'Ziconotide, Methoxyflurane', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (90080, 'Donepezil', 'Cisapride', 'Moderate', 'Acetylcholinesterase inhibitors may have vagotonic effects on the sinoatrial and atrioventricular nodes, which occasionally manifest as bradycardia or heart block (<2%). Because bradycardia is a risk factor for torsade de pointes, a theoretical risk exists when combined with agents that prolong the QT interval.', 'DDInter', 'Caution is advised when acetylcholinesterase inhibitors are used with drugs that can prolong the QT interval. Patients should be monitored for bradycardia, atrioventricular block and syncope, and advised to seek medical attention if they experience dizziness, lightheadedness, fainting, shortness of breath, or slow or irregular heartbeat.', 'Synergism', 'Caution is advised when acetylcholinesterase inhibitors are used with drugs that can prolong the QT interval.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/90182/', '[1] "Product Information. Razadyne (galantamine)." Johnson and Johnson Medical Inc, Arlington, TX.[2] "Product Information. Exelon (rivastigmine)" Novartis Pharmaceuticals, East Hanover, NJ.[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] "Product Information. Aricept (donepezil)." Pfizer US Pharmaceuticals, New York, NY.[5] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[6] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."', 'Memantine, Ginkgo biloba, Aducanumab', NULL, 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (90081, 'Donepezil', 'Clotrimazole', 'Minor', 'Coadministration with inhibitors of CYP450 2D6 and/or 3A4 may increase the plasma concentrations of donepezil, which is primarily metabolized by these isoenzymes.', 'DDInter', '-', 'Metabolism', '-', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/90183/', '[1] Tiseo PJ, Perdomo CA, Friedhoff LT "Concurrent administration of donepezil HCl and ketoconazole: assessment of pharmacokinetic changes following single and multiple doses." Br J Clin Pharmacol 46 (1998): 30-4[2] "Product Information. Aricept (donepezil)." Pfizer US Pharmaceuticals, New York, NY.[3] Rojas-Fernandez C, Fisher C "Drug interactions and donepezil." J Am Geriat Soc 40 (2000): 597-8', 'Selenium Sulfide, Thiabendazole, Ciclopirox, Econazole, Flucytosine, Tioconazole, Chlorphenesin, Tetracycline, Minocycline, Amphotericin B, Epinephrine, More', 'Rivastigmine, Galantamine, Tacrine, Memantine, Ginkgo biloba, Aducanumab', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (90082, 'Donepezil', 'Cyclizine', 'Moderate', 'Acetylcholinesterase inhibitors (e.g., donepezil, galantamine, physostigmine, rivastigmine, tacrine) may antagonize the effects of anticholinergic agents and other agents that rely partially on their anticholinergic activity for therapeutic effects (e.g., some antiparkinsonian and antiemetic/antivertigo agents; class IA antiarrhythmics). By inhibiting the metabolism of acetylcholine, more of the neurotransmitter may be available to compete at muscarinic receptors, the site of action of anticholinergic agents. Conversely, anticholinergic agents may negate the already small pharmacologic benefits of acetylcholinesterase inhibitors in the treatment of dementia. These agents may also adversely affect elderly patients in general. Clinically significant mental status changes associated with anticholinergic agents can range from mild cognitive impairment to delirium, and patients with Alzheimer''s disease and other dementia are especially sensitive.', 'DDInter', 'Anticholinergic agents should generally be avoided in patients with Alzheimer''s disease or other cognitive impairment, regardless of whether they are receiving an acetylcholinesterase inhibitor. For patients requiring treatment to counteract adverse effects of acetylcholinesterase inhibitor therapy (e.g., gastrointestinal intolerance, urinary problems), an agent without anticholinergic properties should be used whenever possible. Otherwise, a dosage reduction, slower titration, or even discontinuation of the acetylcholinesterase inhibitor should be considered. For patients who are already receiving an acetylcholinesterase inhibitor with anticholinergic agents, every attempt should be made to discontinue the latter or substitute them with less anticholinergic alternatives. Caution is required, however, since anticholinergic withdrawal may occur. Seizures have been reported following abrupt discontinuation of anticholinergics during acetylcholinesterase inhibitor therapy.', 'Antagonism', 'Anticholinergic agents should generally be avoided in patients with Alzheimer''s disease or other cognitive impairment, regardless of whether they are receiving an acetylcholinesterase inhibitor.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/90184/', '[1] "Product Information. Aricept (donepezil)." Pfizer US Pharmaceuticals, New York, NY.[2] Roe CM, Anderson MJ, Spivack B "Use of anticholinergic medications by older adults with dementia." J Am Geriatr Soc 50 (2002): 836-42[3] Katz IR, Sands LP, Bilker W, DiFilippo S, Boyce A, D''Angelo K "Identification of medications that cause cognitive impairment in older people: the case of oxybutynin chloride." J Am Geriatr Soc 46 (1998): 8-13[4] Fick DM, Cooper JW, Wade WE, Waller JL, Maclean JR, Beers MH "Updating the Beers criteria for potentially inappropriate medication use in older adults: results of a US consensus panel of experts." Arch Intern Med 163 (2003): 2716-2724[5] Beers MH, Ouslander JG, Rollingher I, Reuben DB, Brooks J, Beck JC "Explicit criteria for determining inappropriate medication use in nursing home residents." Arch Intern Med 151 (1991): 1825-32[6] "Product Information. Cognex (tacrine)." Parke-Davis, Morris Plains, NJ.[7] "Product Information. Reminyl (galantamine)" Janssen Pharmaceuticals, Titusville, NJ.[8] Edwards KR, O''Connor JT "Risk of delirium with concomitant use of tolterodine and acetylcholinesterase inhibitors." J Am Geriatr Soc 50 (2002): 1165-6[9] Carnahan RM, Lund BC, Perry PJ, Chrischilles EA "The concurrent use of anticholinergics and cholinesterase inhibitors: rare event or common practice?" J Am Geriatr Soc 52 (2004): 2082-7', 'Ginkgo biloba, Aducanumab', 'Levocetirizin