INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (92350, 'Dronedarone', 'Copanlisib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 and P-glycoprotein (P-gp) may increase the plasma concentrations of copanlisib, which is a substrate of both the isoenzyme and efflux transporter.', 'DDInter', 'Caution is advised when copanlisib is used with CYP450 3A4 inhibitors. Patients should be monitored for potentially increased adverse effects such as nausea, vomiting, diarrhea, stomatitis, hyperglycemia, hypertension, noninfectious pneumonitis, cutaneous reactions (e.g., exfoliative dermatitis, maculopapular rash), anemia, neutropenia, thrombocytopenia, and infections. Dosage adjustments for copanlisib may be necessary and should be done in accordance with the product labeling.', 'Metabolism', 'Caution is advised when copanlisib is used with CYP450 3A4 inhibitors.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/92454/', '[1] "Product Information. Aliqopa (copanlisib)." Bayer Pharmaceutical Inc, West Haven, CT.[2] "Product Information. Aliqopa (copanlisib)." Bayer Pharmaceutical Inc, West Haven, CT.', 'Quinidine, Amiodarone, Lidocaine, Propafenone, Tocainide, Procainamide, Flecainide, Disopyramide, Mexiletine, Dofetilide, Bretylium, More', 'Regorafenib, Alpelisib, Trametinib, Erlotinib, Erdafitinib, Dacomitinib, Tivozanib, Alectinib', 1767369485);
