INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (92572, 'Dronedarone', 'Penbutolol', 'Moderate', 'Coadministration with dronedarone may increase the plasma concentrations of some beta-blockers. The mechanism is dronedarone inhibition of CYP450 2D6, the isoenzyme responsible for the metabolic clearance of beta-blockers like metoprolol and propranolol. Pharmacodynamically, beta-blockers can potentiate the depressant effects of dronedarone on the sinus and AV nodes.', 'DDInter', 'Caution is advised if dronedarone is coadministered with a beta-blocker, including ophthalmic formulations. The dosage of the beta-blocker should start low and increased only after ECG verification of good tolerability. Patients should be advised to promptly report potential symptoms of toxicity such as excessive fatigue, fainting, difficulty breathing, chest pain or tightness, bradycardia, or arrhythmia.', 'Metabolism', 'Caution is advised if dronedarone is coadministered with a beta-blocker, including ophthalmic formulations.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/92676/', '[1] "Product Information. Multaq (dronedarone)." sanofi-aventis , Bridgewater, NJ.[2] "Product Information. Multaq (dronedarone)." sanofi-aventis , Bridgewater, NJ.', 'Procainamide, Mexiletine, Dofetilide, Bretylium, Moricizine, Ibutilide', NULL, 1767369485);
