INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (94231, 'Tamsulosin', 'Silodosin', 'Moderate', 'Additive pharmacodynamic effects may be expected when relatively selective alpha-1A adrenoreceptor antagonists are coadministered with other alpha-blockers. Clinical data have not been reported, but there may be an increased risk and/or severity of adverse effects related to blockade of alpha-1 adrenergic receptors in the peripheral vasculature such as postural hypotension, dizziness, headache, syncope, priapism, and nasal congestion. These effects have occurred with alpha-1A adrenoreceptor antagonists even when given alone for the treatment of benign prostatic hyperplasia, despite relative selectivity for the 1A subtype of alpha-1 adrenoreceptors located in prostatic and bladder tissues.', 'DDInter', 'The use of relatively selective alpha-1A adrenoreceptor antagonists in combination with other alpha-blockers should be avoided.', 'Synergism', 'The use of relatively selective alpha-1A adrenoreceptor antagonists in combination with other alpha-blockers should be avoided.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/94335/', '[1] "Product Information. Flomax (tamsulosin)." Boehringer-Ingelheim, Ridgefield, CT.[2] "Product Information. Rapaflo (silodosin)." Watson Pharmaceuticals, Corona, CA.[3] "Product Information. Rapaflo (silodosin)." Watson Pharmaceuticals, Corona, CA.', 'Finasteride, Silodosin, Dutasteride, Solifenacin, Tinidazole, Ascorbic acid, Hydroflumethiazide, Chlorothiazide, Metronidazole, Simeprevir, Rosuvastatin, More', 'Tamsulosin, Solifenacin', 1767369485);
