INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (94388, 'Duvelisib', 'Lyme disease vaccine (recombinant OspA)', 'Moderate', 'The administration of Lyme disease vaccine to immunosuppressed patients may be associated with a diminished or suboptimal immunologic response due to antibody inhibition. Such patients may include those who have recently received or are receiving immunosuppressive agents, antilymphocyte globulins, alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids.', 'DDInter', 'The manufacturer suggests considering deferral of vaccination against Lyme disease until 3 months after discontinuation of immunosuppressive therapy. Vaccines may generally be administered to patients receiving corticosteroids as replacement therapy (e.g., for Addison''s disease).', 'Others', 'The manufacturer suggests considering deferral of vaccination against Lyme disease until 3 months after discontinuation of immunosuppressive therapy.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/94492/', '[1] "Product Information. Lymerix (Lyme disease vaccine)." SmithKline Beecham, Philadelphia, PA.', 'Entrectinib, Pralsetinib, Dabrafenib, Asciminib, Dacomitinib, Pexidartinib, Pacritinib, Tepotinib, Fedratinib, Larotrectinib, Vemurafenib, More', NULL, 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (94389, 'Duvelisib', 'Macitentan', 'Moderate', 'Coadministration with duvelisib may increase the plasma concentrations of drugs that are primarily metabolized by the CYP450 3A4 isoenzyme. The mechanism is reduced clearance due to inhibition of CYP450 3A4 by duvelisib. The interaction may be particularly important for sensitive CYP450 3A4 substrates or those that demonstrate a narrow therapeutic index.', 'DDInter', 'Caution is advised if duvelisib is used in combination with sensitive CYP450 3A4 substrates or those that demonstrate a narrow therapeutic index (e.g., ergot alkaloids, colchicine, fentanyl, macrolide immunosuppressants, midazolam, triazolam, vinca alkaloids).', 'Metabolism', 'Caution is advised if duvelisib is used in combination with sensitive CYP450 3A4 substrates or those that demonstrate a narrow therapeutic index (e.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/94493/', '[1] "Product Information. Copiktra (duvelisib)." Verastem, Inc., Needham, MA.', 'Riociguat, Ambrisentan, Metyrosine', 'Dabrafenib, Acalabrutinib, Dacomitinib, Tepotinib, Avapritinib, Bosutinib, Selumetinib, Vandetanib, Cobimetinib, Capmatinib, Ibrutinib, More', 1767369485);
