INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (94518, 'Duvelisib', 'Sirolimus', 'Moderate', 'Coadministration with drugs that are inhibitors of CYP450 3A4 may increase the blood concentrations of the macrolide immunosuppressants sirolimus and tacrolimus, both of which are metabolized by the isoenzyme.', 'DDInter', 'The possibility of prolonged and/or increased pharmacologic effects of macrolide immunosuppressant therapy should be considered, including adverse effects such as fever, infection, diarrhea, hypokalemia, anemia, thrombocytopenia, leukopenia, and hyperlipidemia. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy.', 'Metabolism', 'The possibility of prolonged and/or increased pharmacologic effects of macrolide immunosuppressant therapy should be considered, including adverse effects such as fever, infection, diarrhea, hypokalemia, anemia, thrombocytopenia, leukopenia, and hyperlipidemia.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/94622/', '[1] Christians U, Schmidt G, Bader A, et al "Identification of drugs inhibiting the in vitro metabolism of tacrolimus by human liver microsomes." Br J Clin Pharmacol 41 (1996): 187-90[2] "Product Information. Parlodel (bromocriptine)." Sandoz Pharmaceuticals Corporation, East Hanover, NJ.[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] "Product Information. Rapamune (sirolimus)" Wyeth-Ayerst Laboratories, Philadelphia, PA.[5] "Product Information. Prograf (tacrolimus)." Fujisawa, Deerfield, IL.[6] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[7] "Product Information. Rapamune (sirolimus)" Wyeth-Ayerst Laboratories, Philadelphia, PA.', '