INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (94619, 'Digitoxin', 'Minocycline', 'Moderate', 'Tetracyclines may increase serum levels of orally administered digoxin in about 10% of the population. The mechanism may be related to changes in intestinal flora that alter the absorption of digoxin. The risk of an interaction may be less with digoxin solution in capsules because absorption occurs in the upper GI tract.', 'DDInter', 'Clinical monitoring of patient response and tolerance, including laboratory serum digoxin levels is recommended. Digoxin dosage reductions may be required. Patients should be advised to notify their physicians if they experience nausea, anorexia, visual changes, slow pulse, or irregular heartbeats.', 'Absorption', 'Clinical monitoring of patient response and tolerance, including laboratory serum digoxin levels is recommended.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/94723/', '[1] Lindenbaum J, Rund DG, Butler VP Jr, Tse-Eng D, Saha JR "Inactivation of digoxin by the gut flora: reversal by antibiotic therapy." N Engl J Med 305 (1981): 789-94[2] Rodin SM, Johnson BF "Pharmacokinetic interactions with digoxin." Clin Pharmacokinet 15 (1988): 227-44[3] Lindenbaum J, Tse-Eng D, Butler VP, Rund DG "Urinary excretion of reduced metabolites of digoxin." Am J Med 71 (1981): 67-74[4] "Product Information. Lanoxicaps (digoxin)." Glaxo Wellcome, Research Triangle Park, NC.[5] "Product Information. Achromycin (tetracycline)." Lederle Laboratories, Wayne, NJ.[6] "Product Information. Declomycin (demeclocycline)." Lederle Laboratories, Wayne, NJ.', 'Clindamycin, Vitamin A, Isotretinoin, Chloramphenicol, Fluorometholone, Tretinoin, Dapsone, Azelaic acid, Adapalene, Tigecycline, Sarecycline, More', NULL, 176