INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (94725, 'Isradipine', 'Amprenavir', 'Moderate', 'Coadministration with amprenavir or its prodrug, fosamprenavir, may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by amprenavir.', 'DDInter', 'Caution is advised if amprenavir must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever amprenavir is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if amprenavir must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/94829/', '[1] "Product Information. Agenerase (amprenavir)." Glaxo Wellcome, Research Triangle Pk, NC.[2] "Product Information. Lexiva (fosamprenavir)." GlaxoSmithKline, Research Triangle Park, NC.[3] "Product Information. Agenerase (amprenavir)." Glaxo Wellcome, Research Triangle Pk, NC.[4] Demarles D, Gillotin C, Bonaventure-Paci S, Vincent I, Fosse S, Taburet AM "Single-dose pharmacokinetics of amprenavir coadministered with grapefruit juice." Antimicrob Agents Chemother 46 (2002): 1589-1590', 'Clevidipine, Mibefradil, Celecoxib', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Rilpivirine, Dolutegravir, Oseltamivir, More', 1767369485);
