INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (96413, 'Elagolix', 'Ramelteon', 'Moderate', 'Coadministration with inducers of CYP450 isoenzymes may decrease the plasma concentrations and pharmacologic effects of ramelteon, which is metabolized by CYP450 1A2 and, to a lesser extent, by CYP450 3A4 and the 2C subfamily of isoenzymes.', 'DDInter', 'The efficacy of ramelteon may be reduced when prescribed with potent inducers of CYP450 isoenzymes such as carbamazepine, enzalutamide, phenobarbital, phenytoin, rifampin, and St. John''s wort. Other known inducers include aminoglutethimide, bexarotene, bosentan, dabrafenib, dexamethasone, efavirenz, etravirine, mitotane, modafinil, nafcillin, nevirapine, rifabutin, rifapentine, barbiturates and various other anticonvulsants, although the extent to which they interact with ramelteon is unknown.', 'Metabolism', 'The efficacy of ramelteon may be reduced when prescribed with potent inducers of CYP450 isoenzymes such as carbamazepine, enzalutamide, phenobarbital, phenytoin, rifampin, and St.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/96517/', '[1] "Product Information. Rozerem (ramelteon)." Takeda Pharmaceuticals America, Lincolnshire, IL.[2] "Product Information. Diabinese (chlorpropamide)." Pfizer US Pharmaceuticals, New York, NY.[3] Skillman TG, Feldman JM "The pharmacology of sulfonylureas." Am J Med 70 (1981): 361-72[4] Jerntorp P, Almer LO "Chlorpropamide-alcohol flushing in relation to macroangiopathy and peripheral neuropathy in non-insulin dependent diabetes." Acta Med Scand 656 (1981): 33-6[5] "Product Information. Glucotrol (glipizide)." Pfizer US Pharmaceuticals, New York, NY.[6] "Position Statement: evidence-based nutrition principles and recommendations for the trea