INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (96652, 'Flibanserin', 'Levetiracetam', 'Moderate', 'The central nervous system (CNS) effects of flibanserin such as somnolence and fatigue may be potentiated by concomitant use of other agents with CNS depressant effects. The risk of CNS depression is increased if flibanserin is taken during waking hours or with alcohol or other CNS depressants.', 'DDInter', 'Patients receiving flibanserin with other agents that can cause CNS depression should be apprised of the potential for increased adverse effects such as drowsiness, dizziness, lightheadedness, and fatigue. Patients should not drive, operate machinery, or engage in hazardous activities requiring mental alertness and motor coordination for at least 6 hours after taking flibanserin.', 'Synergism', 'Patients receiving flibanserin with other agents that can cause CNS depression should be apprised of the potential for increased adverse effects such as drowsiness, dizziness, lightheadedness, and fatigue.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/96756/', '[1] "Product Information. Addyi (flibanserin)." Sprout Pharmaceuticals, Raleigh, NC.[2] "Product Information. Addyi (flibanserin)." Sprout Pharmaceuticals, Raleigh, NC.[3] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[4] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[5] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[6] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 'Fenflura