INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (96823, 'Eletriptan', 'Miconazole', 'Moderate', 'Coadministration with miconazole may increase the plasma concentrations of drugs that are substrates of CYP450 3A4. The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by miconazole, which is thought to be a relatively potent inhibitor of the isoenzyme. Although systemic absorption following mucous membrane exposure is limited, the potential for interaction with drugs metabolized by CYP450 3A4 cannot be ruled out.', 'DDInter', 'Caution is advised if miconazole must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever miconazole is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if miconazole must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/96927/', '[1] "Product Information. ORAVIG (miconazole)." Strativa Pharmaceuticals, a Division of Par Pharmaceuticals, Inc., Woodcliff Lake, NJ.', 'Frovatriptan, Rimegepant, Eptinezumab, Lasmiditan, Atogepant, Rizatriptan, Naratriptan, Galcanezumab, Zolmitriptan, Sumatriptan, Erenumab, More', 'Rifamycin, Kanamycin, Amphotericin B, Polymyxin B, Nystatin, Vancomycin, Streptomycin, Fidaxomicin, Rifaximin, Paromomycin, Neomycin, More', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (96824, 'Eletriptan', 'Mifepristone', 'Major', 'Coadministration with mifepristone may significantly increase the plasma concentrations of drugs that are primarily metabolized by the CYP450 3A4 isoenzyme. The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by mifepristone.', 'DDInter', 'When mifepristone is used daily to control hyperglycemia secondary to hypercortisolism in patients with endogenous Cushing''s syndrome, concomitant use of CYP450 3A4 substrates with narrow therapeutic ranges and high first-pass effect is considered contraindicated. These drugs include lovastatin, simvastatin, cyclosporine, ergot derivatives, fentanyl, oral midazolam, triazolam, pimozide, quinidine, sildenafil, sirolimus, and tacrolimus. The contraindication does not apply when mifepristone is used on a limited basis to terminate a pregnancy, but caution and close monitoring are advisable. Because mifepristone is eliminated slowly from the body, drug interactions may be observed for a prolonged period following discontinuation (approximately 2 to 3 weeks if mifepristone had been administered chronically to steady state).', 'Metabolism', 'When mifepristone is used daily to control hyperglycemia secondary to hypercortisolism in patients with endogenous Cushing''s syndrome, concomitant use of CYP450 3A4 substrates with narrow therapeutic ranges and high first-pass effect is considered contraindicated.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/96928/', '[1] "Product Information. Korlym (mifepristone)." Corcept Therapeutics Incorporated, Menlo Park, CA.[2] "Product Information. Mifeprex (mifepristone)" Danco Laboratories, New York, NY.[3] He K, Woolf TF, Hollenberg PF "M