INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (96870, 'Eletriptan', 'Telaprevir', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of eletriptan, which is primarily metabolized by the isoenzyme. Clinically, this interaction may result in increased risk of vasospastic reactions associated with the use of 5-HT1 receptor agonists, such as coronary artery vasospasm, peripheral vascular ischemia, and colonic ischemia.', 'DDInter', 'Eletriptan should not be used within at least 72 hours of treatment with potent CYP450 3A4 inhibitors. Alternatively, other 5-HT1 receptor agonists that are not metabolized by CYP450 3A4 may be considered, such as frovatriptan, naratriptan, rizatriptan, sumatriptan, and zolmitriptan.', 'Metabolism', 'Eletriptan should not be used within at least 72 hours of treatment with potent CYP450 3A4 inhibitors.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/96974/', '[1] "Product Information. Relpax (eletriptan)." Pfizer U.S. Pharmaceuticals, New York, NY.[2] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[3] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 'Frovatriptan, Eptinezumab, Lasmiditan, Rizatriptan, Naratriptan, Galcanezumab, Zolmitriptan, Sumatriptan, Clonidine, Erenumab, Fremanezumab', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Rilpivirine, More', 1767369485);
