INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (106091, 'Ocrelizumab', 'Methotrexate', 'Moderate', 'The use of the CD20-directed cytolytic antibody ocrelizumab with other immune-modulating or immunosuppressive therapy, including immunosuppressant doses of corticosteroids may increase the risk of infections.', 'DDInter', 'The increased risk of additive immunosuppression should be considered if coadministering ocrelizumab with other immunosuppressive therapy. In addition, the prolonged immunosuppressant effects and mode of action of other immunosuppressant drugs such as daclizumab, fingolimod, natalizumab, teriflunomide, or mitoxantrone should be considered prior to starting ocrelizumab therapy. Patients should be advised to notify their doctor if they develop signs or symptoms of infection, including upper or lower respiratory tract infection, skin infection, herpes related infection, or PML.', 'Synergism', 'The increased risk of additive immunosuppression should be considered if coadministering ocrelizumab with other immunosuppressive therapy.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/106214/', '[1] "Product Information. Ocrevus (ocrelizumab)." Genentech, South San Francisco, CA.', 'Apremilast, Pirfenidone, Belumosudil, Teprotumumab, Ocrelizumab, Emapalumab, Ravulizumab, Pegcetacoplan, Eculizumab, Sutimlimab', 'Antithymocyte immunoglobulin (rabbit), Secukinumab, Anifrolumab, Ravulizumab, Methotrexate, Abatacept, Apremilast, Inebilizumab, Belumosudil, Belimumab, Emapalumab, More', 1767369485);
