INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (109254, 'Irbesartan', 'Quinapril', 'Major', 'Coadministration of an ACE inhibitor in combination with an angiotensin II receptor antagonist may increase the risk of hyperkalemia, hypotension, syncope, and renal dysfunction due to additive or synergistic effects on the renin-angiotensin system.', 'DDInter', 'Dual blockade of the renin-angiotensin-aldosterone system by adding an ACE inhibitor to an angiotensin II receptor antagonist is not recommended, especially in patients with diabetic nephropathy. Routine monitoring of electrolytes and renal function may be indicated in the elderly or patients with worsening heart failure or a risk for dehydration. Potassium supplementation should generally be avoided unless it is closely monitored, and patients should be advised to seek medical attention if they experience signs and symptoms of hyperkalemia such as weakness, listlessness, confusion, tingling of the extremities, and irregular heartbeat.', 'Synergism', 'Dual blockade of the renin-angiotensin-aldosterone system by adding an ACE inhibitor to an angiotensin II receptor antagonist is not recommended, especially in patients with diabetic nephropathy.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/109378/', '[1] "Product Information. Diovan (valsartan)." Novartis Pharmaceuticals, East Hanover, NJ.[2] "Product Information. Micardis (telmisartan)." Boehringer-Ingelheim, Ridgefield, CT.[3] Rossing K, Jacobsen P, Pietraszek L, Parving HH "Renoprotective effects of adding angiotensin II receptor blocker to maximal recommended doses of ACE inhibitor in diabetic nephropathy: a randomized double-blind crossover trial." Diabetes Care 26 (2003): 2268-74[4] ONTARGET Investigators, Yusuf S, Te