INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (109436, 'Dalfopristin', 'Lurbinectedin', 'Major', 'Coadministration with a moderate CYP450 3A4 inhibitor is predicted to increase lurbinectedin systemic exposure, which may increase the incidence and severity of adverse reactions to lurbinectedin, such as myelosuppression and hepatotoxicity. Concomitant use of lurbinectedin and moderate CYP450 3A4 inhibitors should generally be avoided. If the coadministration of lurbinectedin with a moderate CYP450 3A4 inhibitor cannot be avoided, a dose reduction for lurbinectedin may be considered.', 'DDInter', 'Close clinical and laboratory monitoring is advised whenever a moderate inhibitor is added to or withdrawn from therapy. Patients should be advised to contact their physician if they experience symptoms suggestive of myelosuppression or hepatotoxicity, such as fever or any other signs of infection, unusual bruising or bleeding; nausea or vomiting, or pain on the right side of the abdomen.', 'Metabolism', 'Close clinical and laboratory monitoring is advised whenever a moderate inhibitor is added to or withdrawn from therapy.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/109560/', '[1] "Product Information. Zepzelca (lurbinectedin)." Jazz Pharmaceuticals, Palo Alto, CA.[2] "Product Information. Zepzelca (lurbinectedin)." Jazz Pharmaceuticals, Palo Alto, CA.', 'Pertuzumab, Oxaliplatin, Bortezomib, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Altretamine, Tagraxofusp, Pegaspargase, Ixazomib, Sotorasib, More', NULL, 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (109437, 'Dalfopristin', 'Macitentan', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of macitentan, which is primarily metabolized by the isoenzyme.', 'DDInter', 'Caution is advisable if macitentan is used with inhibitors of CYP450 3A4. The product labeling recommends avoiding concomitant use with potent inhibitors (e.g., protease inhibitors, clarithromycin, cobicistat, conivaptan, delavirdine, itraconazole, ketoconazole, nefazodone, posaconazole, voriconazole), but no suggestions are given regarding use with less potent inhibitors. It may be reasonable to monitor therapeutic response to macitentan more closely.', 'Metabolism', 'Caution is advisable if macitentan is used with inhibitors of CYP450 3A4.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/109561/', '[1] "Product Information. Opsumit (macitentan)." Actelion Pharmaceuticals US Inc, South San Francisco, CA.', 'Riociguat, Ambrisentan, Metyrosine', NULL, 1767369485);
