INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (109466, 'Dalfopristin', 'Olaparib', 'Major', 'Coadministration with moderate inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of olaparib, which is primarily metabolized by the isoenzyme. Increased exposure to olaparib may increase the risk of adverse effects such as hematologic toxicity, nausea, vomiting, diarrhea, anorexia, dyspepsia, and abdominal pain or discomfort.', 'DDInter', 'Concomitant use of olaparib with moderate CYP450 3A4 inhibitors should be avoided whenever possible. If coadministration is required, dose reductions are recommended as follows: the olaparib tablet dosage should be reduced to 150 mg twice a day; the olaparib capsule dosage should be reduced to 200 mg twice a day. Specific dose recommendations for each formulation should be followed as the tablet and capsule formulations are not interchangeable due to differences in bioavailability. Once the CYP450 3A4 inhibitor has been discontinued for 3 to 5 elimination half-lives, the usual olaparib dose should be resumed.', 'Metabolism', 'Concomitant use of olaparib with moderate CYP450 3A4 inhibitors should be avoided whenever possible.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/109590/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Lynparza (olaparib)." Astra-Zeneca Pharmaceuticals, Wilmington, DE.[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] "Product Information. Lynparza (olaparib)." Astra-Zeneca Pharmaceuticals, Wilmington, DE.[5] Cerner Multum, Inc. "Australian Product Information." O 0[6] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 'Pertuzumab, Oxaliplatin, Bortezomib, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Altretamine, Tagraxofusp, Pegaspargase, Ixazomib, Sotorasib, More', NULL, 1767369485);
