INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (111332, 'Phenytoin', 'Nitrous acid', 'Major', 'Sodium nitrite can cause methemoglobin formation, which diminishes oxygen-carrying capacity of the blood. Coadministration with other agents that are also associated with methemoglobinemia including local anesthetics (e.g., benzocaine, lidocaine, prilocaine), antimalarials (e.g., chloroquine, primaquine, quinine, tafenoquine), nitrates and nitrites, sulfonamides, acetaminophen, aminosalicylic acid, dapsone, dimethyl sulfoxide, flutamide, metoclopramide (primarily in infants), nitrofurantoin (primarily in infants), phenazopyridine, phenobarbital, phenytoin, and rasburicase may increase the risk.', 'DDInter', 'Sodium nitrite should be used with caution in the presence of other methemoglobin-inducing drugs. Patients should be closely monitored to ensure adequate perfusion and oxygenation during treatment with sodium nitrite. Methemoglobin levels should be monitored and oxygen administered whenever possible.', 'Synergism', 'Sodium nitrite should be used with caution in the presence of other methemoglobin-inducing drugs.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/111462/', '[1] "Product Information. Sodium Nitrite (sodium nitrite)." Hope Pharmaceuticals, Scottsdale, AZ.[2] Rehman HU "Methemoglobinemia." West J Med 175 (2001): 193-6[3] Coleman MD, Coleman NA "Drug-induced methaemoglobinaemia: treatment issues." Drug Saf 14 (1996): 394-405', 'Brivaracetam, Topiramate, Ezogabine, Cenobamate, Lamotrigine, Methylphenobarbital, Ethosuximide, Lacosamide, Levetiracetam, Tiagabine, Oxcarbazepine, More', 'Hydroxocobalamin, Fomepizole, Ipecac, Dexrazoxane, Oxygen, Deferoxamine, Arginine, Methionine, Deferiprone, Potassium Iodide, Physostigmine, More', 1767369485);
