INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (111846, 'Abametapir (topical)', 'Velpatasvir', 'Moderate', 'Based on in vitro inhibition data, a single topical application of abametapir lotion may increase plasma concentrations of drugs that are substrates of CYP450 3A4, CYP450 2B6, and CYP450 1A2 isoenzymes and increase their systemic concentrations. The proposed mechanism is high and prolonged systemic exposure to the metabolite abametapir carboxyl, which has been shown to be an in vitro inhibitor of CYP450 3A4, CYP450 2B6, and CYP450 1A2.', 'DDInter', 'Use of CYP450 3A4, CYP450 2B6, and CYP450 1A2 substrates should generally be avoided within 2 weeks after topical application of abametapir lotion. If abametapir is used, monitor for increased toxicity of the CYP450 3A4, CYP450 2B6, and/or CYP450 1A2 substrate.', 'Metabolism', 'Use of CYP450 3A4, CYP450 2B6, and CYP450 1A2 substrates should generally be avoided within 2 weeks after topical application of abametapir lotion.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/111977/', '[1] "Product Information. Xeglyze (abametapir topical)." Dr. Reddy''s Laboratories Inc, Upper Saddle River, NJ.', 'Lamivudine, Glecaprevir, Tenofovir alafenamide, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Lenacapavir, Dolutegravir, Oseltamivir, Acyclovir, More', NULL, 1767369485);
