INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (111850, 'Aluminum hydroxide', 'Velpatasvir', 'Moderate', 'INTERVAL: Concurrent administration of velpatasvir with drugs that elevate gastric pH such as antacids may reduce its gastrointestinal absorption. Velpatasvir exhibits pH-dependent solubility, with increased solubility at lower pH. Drugs that increase gastric pH are expected to decrease the plasma concentration of velpatasvir.', 'DDInter', 'If an antacid is required, the antacid should be taken 4 hours before or 4 hours after administration of sofosbuvir-velpatasvir. The same precaution may be applicable to other drugs with antacid effects.', 'Absorption', 'If an antacid is required, the antacid should be taken 4 hours before or 4 hours after administration of sofosbuvir-velpatasvir.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/111981/', '[1] "Product Information. Epclusa (sofosbuvir-velpatasvir)." Gilead Sciences, Foster City, CA.', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Valaciclovir, Zanamivir, Lenacapavir, Telaprevir, Etravirine, More', NULL, 1767369485);
