INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (111914, 'Velpatasvir', 'Rosuvastatin', 'Major', 'Coadministration with velpatasvir may significantly increase the plasma concentrations of rosuvastatin. The proposed mechanism is velpatasvir inhibition of the hepatic uptake of rosuvastatin via organic anion transporting polypeptide 1B1 (OATP1B1). Inhibition of breast cancer resistance protein (BCRP)-mediated transport may also contribute.', 'DDInter', 'Rosuvastatin should not exceed a dosage of 10 mg daily when prescribed with sofosbuvir-velpatasvir. Alternatively, fluvastatin or pravastatin may be substituted, as the former is not a substrate of OATP1B1, and the latter has been found to not interact significantly with velpatasvir. All patients receiving statin therapy should be advised to promptly report any unexplained muscle pain, tenderness or weakness, particularly if accompanied by fever, malaise and/or dark colored urine. Therapy should be discontinued if creatine kinase is markedly elevated in the absence of strenuous exercise or if myopathy is otherwise suspected or diagnosed.', 'Metabolism', 'Rosuvastatin should not exceed a dosage of 10 mg daily when prescribed with sofosbuvir-velpatasvir.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/112045/', '[1] "Product Information. Epclusa (sofosbuvir-velpatasvir)." Gilead Sciences, Foster City, CA.', 'Amlodipine, Ramipril, Valsartan, Lisinopril, Perindopril, Niacin, Pravastatin, Acetylsalicylic acid, Ezetimibe, Bempedoic acid, Indapamide, More', 'Paritaprevir, Tenofovir alafenamide, Cabotegravir, Maraviroc, Zanamivir, Lenacapavir, Telaprevir, Rilpivirine, Dolutegravir, Brincidofovir, Elbasvir, More', 1767369485);
