INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (112395, 'Gilteritinib', 'Vardenafil', 'Moderate', 'Gilteritinib may cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 'Caution and clinical monitoring are recommended if gilteritinib is used in combination with other drugs that can prolong the QT interval. Patients should have electrocardiograms (ECGs) performed prior to initiation of treatment with gilteritinib, on days 8 and 15 of cycle 1, and prior to the start of the next two subsequent cycles. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Caution and clinical monitoring are recommended if gilteritinib is used in combination with other drugs that can prolong the QT interval.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/112526/', '[1] "Product Information. Xospata (gilteritinib)." Astellas Pharma US, Inc, Deerfield, IL.', 'Darifenacin, Tadalafil, Ammonium chloride, Yohimbine, Oxybutynin, Dimethyl sulfoxide, Pentosan polysulfate, Calcium chloride, Trospium, Flavoxate, Avanafil, More', 'Acalabrutinib, Dacomitinib, Pexidartinib, Tepotinib, Avapritinib, Selumetinib, Cobimetinib, Capmatinib, Ibrutinib, Afatinib, Tivozanib, More', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (112396, 'Glasdegib', 'Vardenafil', 'Moderate', 'Glasdegib may cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 'Coadministration of glasdegib with other drugs that can prolong the QT interval should generally be avoided. Caution and clinical monitoring are recommended if concomitant use is required. Patients should have electrocardiograms (ECGs) performed prior to initiation of treatment with glasdegib, approximately one week after initiation, and then once monthly for the next two months, although patients with risk factors may require more frequent and ongoing ECG monitoring.', 'Synergism', 'Coadministration of glasdegib with other drugs that can prolong the QT interval should generally be avoided.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/112527/', '[1] "Product Information. Daurismo (glasdegib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.[2] "Product Information. Daurismo (glasdegib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.', 'Fesoterodine, Pentosan polysulfate, Calcium chloride, Tolterodine, Phenazopyridine, Trospium, Sildenafil, Alprostadil, Acetohydroxamic acid, Yohimbine, Flavoxate, More', 'Pertuzumab, Olaparib, Bortezomib, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Tazemetostat, Altretamine, Tagraxofusp, Pegaspargase, Ixazomib, More', 1767369485);
