INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (118327, 'Saquinavir', 'Naldemedine', 'Moderate', 'Coadministration with potent inhibitors of CYP450 3A4 may increase the plasma concentrations of naldemedine, which is primarily metabolized by CYP450 3A4 to nor-naldemedine and to a minor extent by UGT1A3 to naldemedine 3-G. Both metabolites have demonstrated antagonistic activity for opioid receptors, but with less potency than the parent drug. Increased exposure to naldemedine may precipitate opioid withdrawal symptoms such as hyperhidrosis, lacrimation, rhinorrhea, chills, diarrhea, abdominal pain, anxiety, insomnia, irritability, restlessness, and yawning.', 'DDInter', 'Caution is advised during concomitant use of naldemedine with potent inhibitors of CYP450 3A4. Patients should be closely monitored for potential opioid withdrawal symptoms as well as other adverse effects of naldemedine.', 'Metabolism', 'Caution is advised during concomitant use of naldemedine with potent inhibitors of CYP450 3A4.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/118465/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Symproic (naldemedine)." Shionogi USA Inc, Florham Park, NJ.[3] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[4] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.[5] "Product Information. Symproic (naldemedine)." Shionogi USA Inc, Florham Park, NJ.', 'Lamivudine, Paritaprevir, Tenofovir alafenamide, Maraviroc, Cabotegravir, Valaciclovir, Zanamivir, Rilpivirine, Dolutegravir, Oseltamivir, Acyclovir, More',