INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (118328, 'Saquinavir', 'Naloxegol', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of naloxegol, which is primarily metabolized by the isoenzyme. Increased exposure to naloxegol may precipitate opioid withdrawal symptoms such as hyperhidrosis, lacrimation, rhinorrhea, chills, diarrhea, abdominal pain, anxiety, insomnia, irritability, restlessness, and yawning.', 'DDInter', 'Concomitant use of naloxegol with potent CYP450 3A4 inhibitors such as clarithromycin, telithromycin, itraconazole, ketoconazole, posaconazole, voriconazole, conivaptan, idelalisib, nefazodone, cobicistat, delavirdine, and protease inhibitors is considered contraindicated. Some authorities consider concomitant administration of naloxegol and itraconazole to be contraindicated during and for 2 weeks after treatment with itraconazole.', 'Metabolism', 'Concomitant use of naloxegol with potent CYP450 3A4 inhibitors such as clarithromycin, telithromycin, itraconazole, ketoconazole, posaconazole, voriconazole, conivaptan, idelalisib, nefazodone, cobicistat, delavirdine, and protease inhibitors is considered contraindicated.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/118466/', '[1] "Product Information. Movantik (naloxegol)." Astra-Zeneca Pharmaceuticals, Wilmington, DE.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Sporanox (itraconazole)." Janssen Pharmaceutica, Titusville, NJ.[4] Cerner Multum, Inc. "Australian Product Information." O 0[5] "Product Information. Movantik (naloxegol)." Astra-Zeneca Pharmaceuticals, Wilmington, DE.[6] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT