INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (124120, 'Olopatadine (nasal)', 'Molindone', 'Moderate', 'Concurrent use of olopatadine nasal spray with alcohol or other central nervous system (CNS) depressants may result in additive impairment of mental alertness and performance. Somnolence has been reported in some patients using olopatadine nasal spray in clinical trials.', 'DDInter', 'Concomitant use of olopatadine nasal spray with alcohol or other CNS depressants should generally be avoided if possible. In the event that they are used together, patients should be counseled to avoid hazardous activities requiring complete mental alertness and motor coordination until they know how these agents affect them, and to notify their doctor if they experience excessive or prolonged CNS effects that interfere with their normal activities.', 'Synergism', 'Concomitant use of olopatadine nasal spray with alcohol or other CNS depressants should generally be avoided if possible.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/124270/', '[1] "Product Information. Patanase (olopatadine nasal)." Alcon Laboratories Inc, Fort Worth, TX.[2] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[3] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[4] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[5] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.[6] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[7] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[8] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[9] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 'Amisulpride, Pimavanserin', NULL, 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (124121, 'Opicapone', 'Molindone', 'Moderate', 'Agents with central antidopaminergic activity such as phenothiazines, neuroleptics, and certain antiemetics may antagonize the pharmacologic effects of dopaminergic drugs, and vice versa. In addition, the central nervous system depressant and hypotensive effects of these agents may be additively or synergistically increased when taken together.', 'DDInter', 'Concomitant use of dopaminergic drugs with antidopaminergic agents should generally be avoided. If coadministration is necessary, patients should be alerted to the possibility of excessive drowsiness and monitored for potentially diminished therapeutic response to both treatments. Patients treated for Parkinson''s disease should generally avoid antidopaminergic agents, particularly phenothiazines and older neuroleptic agents, since these agents may cause extrapyramidal reactions and exacerbate the symptoms of Parkinson''s disease. Likewise, patients with a major psychotic disorder should ordinarily not be treated with dopaminergic drugs because of the risk of exacerbating the psychosis with an increase in central dopaminergic tone.', 'Synergism', 'Concomitant use of dopaminergic drugs with antidopaminergic agents should generally be avoided.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/124271/', '[1] Weingarten JC, Thompson TL "The effect of thioridazine on prolactinoma growth in a schizophrenic man: case report." Gen Hosp Psychiatry 7 (1985): 364-6[2] "Product Information. Norprolac (quinagolide)." Ferring Inc, North York, IA.[3] "Product Information. Requip (ropinirole)." SmithKline Beecham, Philadelphia, PA.[4] "Product Information. Reglan (metoclopramide)." Wyeth-Ayer