INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (124577, 'Moxifloxacin', 'Methoxsalen', 'Moderate', 'Concomitant use of methoxsalen with other known photosensitizing agents may increase the risk of photosensitivity reactions. These agents include anthralin, coal tar or coal tar derivatives, griseofulvin, hypericin extracts (e.g., St John''s Wort), fluoroquinolones, phenothiazines, retinoids, halogenated salicylanilides (bacteriostatic soaps), sulfonamides, sulfonylureas, tetracyclines, thiazides, and certain organic staining dyes such as methylene blue, toluidine blue, rose bengal, and methyl orange.', 'DDInter', 'Caution is advised and pharmacologic response to methoxsalen therapy should be carefully monitored if concomitant use of other photosensitizing agents cannot be avoided. Patients should be advised to avoid sun exposure, even through window glass or cloud cover, for at least 8 hours after methoxsalen ingestion and during the 24 hours following photochemotherapy or photopheresis treatment.', 'Synergism', 'Caution is advised and pharmacologic response to methoxsalen therapy should be carefully monitored if concomitant use of other photosensitizing agents cannot be avoided.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/124727/', '[1] "Product Information. Uvadex (methoxsalen)." Therakos Inc, Exton, PA.[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Multum Information Services, Inc. Expert Review Panel"[4] "Product Information. Oxsoralen (methoxsalen)." ICN Pharmaceuticals Inc, Costa Mesa, CA.[5] Royal Australian College of General Practicioners (RACGP), the Pharmaceutical Society of Australia (PSA), the Australasian Society of Clinical and Experimental Pharmacologists and Toxicologists (ASCEPT) "Australian Medicines Handbook. Available from: URL: https://www.amh.net.au/"[6] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[7] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 'Rifamycin, Kanamycin, Ampicillin, Azithromycin, Acyclovir, Netilmicin, Polymyxin B, Famciclovir, Cefuroxime, Trifluridine, Tobramycin, More', 'Acitretin, Etretinate, Trioxsalen, Tazarotene, Calcipotriol, Trioxsalen', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (124578, 'Moxifloxacin', 'Methyl aminolevulinate (topical)', 'Moderate', 'Concomitant use of aminolevulinate topical preparations with other known photosensitizing agents may enhance the phototoxic reaction to photodynamic therapy. Medicinal products with known phototoxic or photoallergic potential include fluoroquinolones, phenothiazines, retinoids, sulfonamides, sulfonylureas, tetracyclines, thiazide diuretics, griseofulvin, and hypericin extracts (e.g., St John''s Wort).', 'DDInter', 'Caution is advised and pharmacologic response to photodynamic therapy should be carefully monitored if concomitant use of other photosensitizing agents cannot be avoided. Patients should be advised to avoid exposure of treated areas to sunlight or bright indoor lights (e.g., examination lamps, operating room lamps, tanning beds, lights at close proximity) during the period between application of aminolevulinic acid or methyl aminolevulinate and photoactivation, and for 48 hours post-illumination. Concomitant use with other topical medicinal products should be avoided.', 'Synergism', 'Caution is advised and pharmacologic response to photodynamic therapy should be carefully monitored if concomitant use of other photosensitizing agents cannot be avoided.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/124728/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Metvixia (methyl aminolevulinate topical)." Galderma Laboratories Inc, Cranbury, NJ.[4] "Product Information. Levulan Kerastick (aminolevulinic acid)" Berlex, Richmond, CA.[5] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish