INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (140064, 'Simeprevir', 'Midazolam', 'Moderate', 'Coadministration with simeprevir may increase the plasma concentrations of oral midazolam and triazolam, which are primarily metabolized by CYP450 3A4. In vivo, simeprevir mildly inhibits intestinal CYP450 3A4, but does not affect hepatic CYP450 3A4 activity.', 'DDInter', 'No empiric dosage adjustment is required for oral midazolam or triazolam when prescribed with simeprevir. However, because these agents have a narrow therapeutic index, it may be appropriate to monitor pharmacologic response and drug levels more closely following initiation or discontinuation of simeprevir, and the dosage adjusted if necessary.', 'Metabolism', 'No empiric dosage adjustment is required for oral midazolam or triazolam when prescribed with simeprevir.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/140225/', '[1] "Product Information. Olysio (simeprevir)." Janssen Pharmaceuticals, Titusville, NJ.[2] Hukkinen SK, Varhe A, Olkkola KT, Neuvonen PJ "Plasma concentrations of triazolam are increased by concomitant ingestion of grapefruit juice." Clin Pharmacol Ther 58 (1995): 127-31[3] Kupferschmidt HHT, Ha HR, Ziegler WH, Meier PJ, Krahenbuhl S "Interaction between grapefruit juice and midazolam in humans." Clin Pharmacol Ther 58 (1995): 20-8[4] Bailey DG, Dresser GR, Kreeft JH, Munoz C, Freeman DJ, Bend JR "Grapefruit-felodipine interaction: Effect of unprocessed fruit and probable active ingredients." Clin Pharmacol Ther 68 (2000): 468-77[5] "Product Information. Valium (diazepam)." Roche Laboratories, Nutley, NJ.[6] "Product Information. Xanax (alprazolam)." Pharmacia and Upjohn, Kalamazoo, MI.[7] "Grapefruit juice interactions with drugs." Med Le