INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (140141, 'Flecainide', 'Midodrine', 'Minor', 'Theoretically, the coadministration with midodrine may result in decreased clearance of drugs that are primarily eliminated by active tubular secretion such as metformin, cimetidine, ranitidine, procainamide, flecainide, quinidine, and triamterene. The proposed mechanism is competitive inhibition of renal excretion by desglymidodrine, the active metabolite of midodrine. Patients receiving midodrine in combination with drugs that undergo active tubular secretion should be monitored for excessive effects of one or both drugs.', 'DDInter', '-', 'Excretion', '-', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/140302/', '[1] "Product Information. ProAmatine (midodrine)." Roberts Pharmaceutical Corporation, Eatontown, NJ.', 'Dobutamine, Norepinephrine, Droxidopa, Amrinone, Angiotensin II, Ephedrine, Milrinone, Phenylephrine, Fenoldopam, Metaraminol, Epinephrine, More', 'Amiodarone, Lidocaine, Tocainide, Propafenone, Disopyramide, Mexiletine, Dofetilide, Bretylium, Moricizine, Ibutilide', 1767369485);
