INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (140259, 'Flutamide', 'Midostaurin', 'Moderate', 'Long-term androgen deprivation therapy can prolong the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. In addition, the extent of drug-induced QT prolongation is dependent on the particular drug(s) involved and dosage(s) of the drug(s).', 'DDInter', 'The benefits of androgen deprivation therapy should be carefully assessed against the potential risk in patients receiving other drugs known to prolong the QT interval. Electrolyte abnormalities should be corrected prior to initiating therapy, and periodic monitoring of electrocardiograms and electrolytes should be considered.', 'Synergism', 'The benefits of androgen deprivation therapy should be carefully assessed against the potential risk in patients receiving other drugs known to prolong the QT interval.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/140420/', '[1] "Product Information. Vantas (histrelin)." Endo Pharmaceuticals (formally Indevus Pharmaceuticals Inc), Lexington, MA.[2] "Product Information. Zoladex (goserelin)." Zeneca Pharmaceuticals, Wilmington, DE.[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] "Product Information. Trelstar (triptorelin)" Pharmacia and Upjohn, Kalamazoo, MI.[5] "Product Information. Firmagon (degarelix)." Ferring Pharmaceuticals Inc, Tarrytown, NY.[6] "Product Information. Lupron (leuprolide)." TAP Pharmaceuticals Inc, Deerfield, IL.[7] "Product Information. Eligard (leuprolide)." Sanofi Winthrop Pharmaceuticals, New York, NY.[8] "Product Information. Plenaxis (abarelix)." Praecis Pharmaceuticals Inc, Waltham, MA.', 'Anastrozole, Fulvestrant, Exemestane, Letrozole', 'Imatinib, Dabrafenib, Acalabrutinib, Dacomitinib, Tepotinib, Fedratinib, Avapritinib, Larotrectinib, Selumetinib, Cobimetinib, Ibrutinib, More', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (140260, 'Fluvastatin', 'Midostaurin', 'Moderate', 'Coadministration with midostaurin may increase the plasma concentrations and the risk of adverse effects of drugs that are substrates of P-glycoprotein (P-gp), breast cancer resistance protein (BCRP), and/or organic anion transporting polypeptide (OATP) 1B1 transporters. The proposed mechanism, based on in vitro data, is decreased clearance due to midostaurin-mediated inhibition of P-gp, BCRP, and/or OATP1B1 transport proteins.', 'DDInter', 'Caution is advised if midostaurin is used concomitantly with drugs that are substrates of P-gp, BCRP, and/or OATP1B1 transport proteins, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever midostaurin is added to or withdrawn from therapy with these drugs. Patients should be monitored for the development of adverse effects.', 'Metabolism', 'Caution is advised if midostaurin is used concomitantly with drugs that are substrates of P-gp, BCRP, and/or OATP1B1 transport proteins, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/140421/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Rydapt (midostaurin)." Novartis Pharmaceuticals, East Hanover, NJ.', 'Gemfibrozil, Fenofibric acid, Mipomersen, Evinacumab, Evolocumab, Dextrothyroxine, Cholestyramine, Clofibrate, Colestipol, Niacin, Fenofibrate, More', 'Acalabrutinib, Dacomitinib, Pacritinib, Tepotinib, Fedratinib, Avapritinib, Bosutinib, Lapatinib, Selumetinib, Ribociclib, Vandetanib, More', 1767369485);
