INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (140433, 'Stiripentol', 'Midostaurin', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of midostaurin and its active metabolites, which are all substrates of the isoenzyme.', 'DDInter', 'Caution is advised when midostaurin is used with CYP450 3A4 inhibitors. Patients should be closely monitored for increased adverse reactions (e.g., nausea, vomiting, diarrhea, edema, hyperglycemia, hyperuricemia, QT prolongation, neutropenia, lymphopenia, thrombocytopenia, anemia), especially during the first week of consecutive midostaurin administration in patients with advanced systemic mastocytosis and during the first week of midostaurin administration in each cycle of chemotherapy in patients with acute myeloid leukemia.', 'Metabolism', 'Caution is advised when midostaurin is used with CYP450 3A4 inhibitors.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/140594/', '[1] Dutreix C, Munarini F, Lorenzo S, Roesel J, Wang Y "Investigation into CYP3A4-mediated drug-drug interactions on midostaurin in healthy volunteers." Cancer Chemother Pharmacol 72 (2013): 1223-34[2] "Product Information. Rydapt (midostaurin)." Novartis Pharmaceuticals, East Hanover, NJ.[3] "Product Information. Rydapt (midostaurin)." Novartis Pharmaceuticals, East Hanover, NJ.[4] "Product Information. Accolate (zafirlukast)." Zeneca Pharmaceuticals, Wilmington, DE.', 'Asciminib, Dacomitinib, Tepotinib, Avapritinib, Selumetinib, Vandetanib, Afatinib, Tivozanib, Selpercatinib, Regorafenib, Alpelisib, More', 'Brivaracetam, Topiramate, Lamotrigine, Ethosuximide, Lacosamide, Levetiracetam, Tiagabine, Ethotoin, Clonazepam, Paramethadione, Perampanel, More', 1767369485);
