INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (140446, 'Telithromycin', 'Midostaurin', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of midostaurin and its active metabolites, which are all substrates of the isoenzyme. The increase in midostaurin concentrations may be particularly pronounced when CYP450 3A4 inhibitors are administered during the first week of midostaurin administration.', 'DDInter', 'Concomitant use of midostaurin with potent CYP450 3A4 inhibitors should generally be avoided. If coadministration is required, patients should be closely monitored for increased adverse reactions (e.g., nausea, vomiting, diarrhea, edema, hyperglycemia, hyperuricemia, QT prolongation, neutropenia, lymphopenia, thrombocytopenia, anemia), especially during the first week of consecutive midostaurin administration in patients with advanced systemic mastocytosis and during the first week of midostaurin administration in each cycle of chemotherapy in patients with acute myeloid leukemia.', 'Metabolism', 'Concomitant use of midostaurin with potent CYP450 3A4 inhibitors should generally be avoided.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/140607/', '[1] Dutreix C, Munarini F, Lorenzo S, Roesel J, Wang Y "Investigation into CYP3A4-mediated drug-drug interactions on midostaurin in healthy volunteers." Cancer Chemother Pharmacol 72 (2013): 1223-34[2] "Product Information. Rydapt (midostaurin)." Novartis Pharmaceuticals, East Hanover, NJ.[3] "Product Information. Rydapt (midostaurin)." Novartis Pharmaceuticals, East Hanover, NJ.[4] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[5] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 'Dirithromycin, Lincomycin, Clindamycin', 'Alpelisib, Trametinib, Dacomitinib, Tivozanib, Alectinib', 1767369485);
