INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (140557, 'Fondaparinux', 'Mifepristone', 'Major', 'Concomitant use of anticoagulants may exacerbate mifepristone-induced vaginal bleeding in women. Prolonged, potentially heavy bleeding lasting approximately 9 to 30 days typically follows mifepristone use in termination of pregnancy. Because it is a progesterone receptor antagonist, mifepristone can also promote unopposed endometrial proliferation that may result in endometrium thickening, cystic dilatation of endometrial glands, and vaginal bleeding when used daily to control hyperglycemia secondary to hypercortisolism in patients with endogenous Cushing''s syndrome.', 'DDInter', 'Concurrent anticoagulant therapy is considered a contraindication in the use of mifepristone for pregnancy termination. When used daily to control hyperglycemia secondary to hypercortisolism in patients with endogenous Cushing''s syndrome, mifepristone treatment should be administered cautiously in women who are receiving anticoagulant therapy. Patients who experience vaginal bleeding should be referred to a gynecologist for further evaluation.', 'Synergism', 'Concurrent anticoagulant therapy is considered a contraindication in the use of mifepristone for pregnancy termination.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/140718/', '[1] "Product Information. Mifeprex (mifepristone)" Danco Laboratories, New York, NY.[2] "Product Information. Korlym (mifepristone)." Corcept Therapeutics Incorporated, Menlo Park, CA.[3] "Product Information. Korlym (mifepristone)." Corcept Therapeutics Incorporated, Menlo Park, CA.[4] "Product Information. Mifeprex (mifepristone)" Danco Laboratories, New York, NY.', 'Alteplase, Dipyridamole, Abciximab, Tirofiban, Caplacizumab, Acetylsalicylic acid, Iloprost, Streptokinase, Eptifibatide, Antithrombin III human, Tenecteplase, More', 'Ulipristal, Danazol, Ospemifene, Raloxifene, Bazedoxifene, Prasterone', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (140558, 'Fosamprenavir', 'Mifepristone', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of mifepristone, which is primarily metabolized by the isoenzyme. The clinical impact of this interaction has not been studied.', 'DDInter', 'Due to its potent antiglucocorticoid effects and potential to cause dose-related prolongation of the QT interval, extreme caution is advised when mifepristone is prescribed in combination with strong CYP450 3A4 inhibitors such as, but not limited to, itraconazole, ketoconazole, posaconazole, voriconazole, clarithromycin, conivaptan, delavirdine, nefazodone, telithromycin, and protease inhibitors. When used daily to control hyperglycemia secondary to hypercortisolism in patients with endogenous Cushing''s syndrome, the dosage of mifepristone should be limited to 300 mg/day and used only when necessary.', 'Metabolism', 'Due to its potent antiglucocorticoid effects and potential to cause dose-related prolongation of the QT interval, extreme caution is advised when mifepristone is prescribed in combination with strong CYP450 3A4 inhibitors such as, but not limited to, itraconazole, ketoconazole, posaconazole, voriconazole, clarithromycin, conivaptan, delavirdine, nefazodone, telithromycin, and protease inhibitors.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/140719/', '[1] "Product Information. Korlym (mifepristone)." Corcept Therapeutics Incorporated, Menlo Park, CA.[2] "Product Information. Mifeprex (mifepristone)" Danco Laboratories, New York, NY.[3] "Product Information. Korlym (mifepristone)." Corcept Therapeutics Incorporated, Menlo Park, CA.[4] "Product Information. Mifeprex (m