INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (140576, 'Griseofulvin', 'Mifepristone', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of mifepristone, which is primarily metabolized by the isoenzyme.', 'DDInter', 'When mifepristone is prescribed daily to control hyperglycemia secondary to hypercortisolism in patients with endogenous Cushing''s syndrome, concomitant use with potent CYP450 3A4 inducers such as carbamazepine, phenobarbital, phenytoin, and rifamycins should generally be avoided. Other, less potent inducers such as aminoglutethimide, barbiturates, bexarotene, bosentan, dabrafenib, efavirenz, nafcillin, nevirapine, somatrem, somatropin, and various other anticonvulsants may also interact. Caution is advised if they are used with mifepristone.', 'Metabolism', 'When mifepristone is prescribed daily to control hyperglycemia secondary to hypercortisolism in patients with endogenous Cushing''s syndrome, concomitant use with potent CYP450 3A4 inducers such as carbamazepine, phenobarbital, phenytoin, and rifamycins should generally be avoided.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/140737/', '[1] "Product Information. Mifeprex (mifepristone)" Danco Laboratories, New York, NY.[2] "Product Information. Korlym (mifepristone)." Corcept Therapeutics Incorporated, Menlo Park, CA.[3] "Product Information. Korlym (mifepristone)." Corcept Therapeutics Incorporated, Menlo Park, CA.[4] "Product Information. Mifeprex (mifepristone)" Danco Laboratories, New York, NY.', 'Terbinafine, Salicylic acid, Selenium Sulfide, Thiabendazole, Nystatin, Ciclopirox, Terbinafine, Econazole, Flucytosine, Tioconazole, Chlorphenesin', 'Danazol, Ospemifene, Raloxifene, Bazedoxifene, Prasterone', 1767369485);
