INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (140593, 'Indinavir', 'Mifepristone', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of mifepristone, which is primarily metabolized by the isoenzyme. The clinical impact of this interaction has not been studied.', 'DDInter', 'Due to its potent antiglucocorticoid effects and potential to cause dose-related prolongation of the QT interval, extreme caution is advised when mifepristone is prescribed in combination with strong CYP450 3A4 inhibitors such as, but not limited to, itraconazole, ketoconazole, posaconazole, voriconazole, clarithromycin, conivaptan, delavirdine, nefazodone, telithromycin, and protease inhibitors. When used daily to control hyperglycemia secondary to hypercortisolism in patients with endogenous Cushing''s syndrome, the dosage of mifepristone should be limited to 300 mg/day and used only when necessary.', 'Metabolism', 'Due to its potent antiglucocorticoid effects and potential to cause dose-related prolongation of the QT interval, extreme caution is advised when mifepristone is prescribed in combination with strong CYP450 3A4 inhibitors such as, but not limited to, itraconazole, ketoconazole, posaconazole, voriconazole, clarithromycin, conivaptan, delavirdine, nefazodone, telithromycin, and protease inhibitors.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/140754/', '[1] "Product Information. Korlym (mifepristone)." Corcept Therapeutics Incorporated, Menlo Park, CA.[2] "Product Information. Mifeprex (mifepristone)" Danco Laboratories, New York, NY.[3] "Product Information. Korlym (mifepristone)." Corcept Therapeutics Incorporated, Menlo Park, CA.[4] "Product Information. Mifeprex (mifep