INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (140625, 'Nintedanib', 'Mifepristone', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein (P-gp) and CYP450 3A4 may increase the plasma concentrations of nintedanib, which is a substrate of the efflux transporter and a minor substrate of the isoenzyme.', 'DDInter', 'Caution is advised if nintedanib is prescribed in combination with P-gp and CYP450 3A4 inhibitors. Pharmacologic response to nintedanib should be monitored more closely whenever a CYP450 3A4/P-gp inhibitor is added to or withdrawn from therapy, and the nintedanib dosing adjusted or interrupted as necessary in accordance with the product labeling. Patients should be closely monitored for increased adverse effects such as liver enzyme and bilirubin elevations, diarrhea, nausea, vomiting, gastrointestinal perforation, bleeding, and arterial thromboembolic events (e.g., myocardial infarction).', 'Metabolism', 'Caution is advised if nintedanib is prescribed in combination with P-gp and CYP450 3A4 inhibitors.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/140786/', '[1] "Product Information. Ofev (nintedanib)." Boehringer Ingelheim, Ridgefield, CT.[2] "Product Information. Ofev (nintedanib)." Boehringer Ingelheim, Ridgefield, CT.', 'Ulipristal, Danazol, Ospemifene, Raloxifene, Bazedoxifene, Prasterone', 'Regorafenib, Alpelisib, Trametinib, Erlotinib, Erdafitinib, Afatinib, Dacomitinib, Tivozanib, Alectinib', 1767369485);
