INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (142104, 'Digitoxin', 'Milnacipran', 'Moderate', 'Use of milnacipran concomitantly with digoxin may be associated with potentiation of adverse hemodynamic effects. Inhibition of norepinephrine and serotonin reuptake by milnacipran can lead to cardiovascular effects including sustained increases in blood pressure and heart rate. However, effects of milnacipran in patients with significant hypertension, arrhythmia, or cardiac disease have not been systematically evaluated.', 'DDInter', 'Coadministration of milnacipran and intravenous digoxin should be avoided. Caution is advised during coadministration with oral digoxin therapy. Blood pressure and heart rate should be measured prior to initiating milnacipran and periodically throughout treatment. A dosage reduction or discontinuation should be considered in patients who experience a sustained increase in blood pressure or heart rate while receiving milnacipran.', 'Synergism', 'Coadministration of milnacipran and intravenous digoxin should be avoided.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/142265/', '[1] "Product Information. Savella (milnacipran)." Forest Pharmaceuticals, St. Louis, MO.[2] "Product Information. Savella (milnacipran)." Forest Pharmaceuticals, St. Louis, MO.', 'Citalopram, Sertraline, Maprotiline, Fluoxetine, St. John''s Wort, Vortioxetine, Levomilnacipran, Doxepin, Duloxetine, Isocarboxazid, Viloxazine, More', NULL, 1767369485);
