INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (142206, 'Propylhexedrine (nasal)', 'Milnacipran', 'Moderate', 'Additive or synergistic effects on blood pressure and heart rate may occur when serotonin-norepinephrine reuptake inhibitors (SNRIs) are combined with sympathomimetic agents such as nasal decongestants, adrenergic bronchodilators, ophthalmic vasoconstrictors, and systemic vasopressors. The use of SNRIs alone has been associated with sustained increases in blood pressure and heart rate, and cases of elevated blood pressure requiring immediate treatment have been reported in postmarketing experience.', 'DDInter', 'Caution is advised if SNRIs are used with other drugs that can increase blood pressure and/or heart rate. Blood pressure and pulse should be measured prior to initiating SNRI therapy and monitored at regular intervals thereafter. Dose reduction or discontinuation of the SNRI should be considered in patients who experience a sustained increase in blood pressure or pulse rate.', 'Synergism', 'Caution is advised if SNRIs are used with other drugs that can increase blood pressure and/or heart rate.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/142367/', '[1] "Product Information. Fetzima (levomilnacipran)." Forest Pharmaceuticals, St. Louis, MO.[2] "Product Information. Cymbalta (duloxetine)." Lilly, Eli and Company, Indianapolis, IN.[3] "Product Information. Nucynta (tapentadol)." PriCara Pharmaceuticals, Raritan, NJ.[4] "Product Information. Effexor (venlafaxine)." Wyeth-Ayerst Laboratories, Philadelphia, PA.[5] "Product Information. Pristiq (desvenlafaxine)." Wyeth Laboratories, Philadelphia, PA.[6] "Product Information. Savella (milnacipran)." Forest Pharmaceuticals, St. Louis, MO.[7] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[8] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.[9] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[10] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[11] "Grapefruit juice interactions with drugs." Med Lett Drugs Ther 37 (1995): 73-4[12] Maish WA, Hampton EM, Whitsett TL, Shepard JD, Lovallo WR "Influence of grapefruit juice on caffeine pharmacokinetics and pharmacodynamics." Pharmacotherapy 16 (1996): 1046-52', 'Citalopram, Sertraline, Maprotiline, Fluoxetine, St. John''s Wort, Vortioxetine, Doxepin, Viloxazine, Amoxapine, Imipramine, Fluvoxamine, More', NULL, 1767369485);
