INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (142736, 'Reserpine', 'Nefazodone', 'Moderate', 'Nefazodone may potentiate the hypotensive effect of many drugs including vasodilators and antihypertensive agents. Orthostatic hypotension may occur.', 'DDInter', 'Caution and close monitoring for altered efficacy and safety are recommended if patients receive nefazodone with agents that can produce significant hypotension such as antihypertensive agents or vasodilators. Patients should be made aware of the possible side effects (e.g., dizziness, lightheadedness, orthostasis) and be cautioned about driving, operating machinery, or performing other hazardous tasks.', 'Synergism', 'Caution and close monitoring for altered efficacy and safety are recommended if patients receive nefazodone with agents that can produce significant hypotension such as antihypertensive agents or vasodilators.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/142899/', '[1] "Product Information. Serzone (nefazodone)." Bristol-Myers Squibb, Princeton, NJ.[2] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[3] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[4] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[5] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 'Citalopram, Duloxetine, Sertraline, Paroxetine, Desvenlafaxine, Fluoxetine, St. John''s Wort, Viloxazine, Milnacipran, Vortioxetine, Tryptophan, More', NULL, 1767369485);
