INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (143044, 'Halofantrine', 'Nelfinavir', 'Major', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of halofantrine, resulting in an increased risk of QT interval prolongation and ventricular arrhythmias. The mechanism is inhibition of CYP450 3A4, the isoenzyme responsible for the metabolic clearance of halofantrine. Halofantrine has been associated with QT interval prolongation, ventricular arrhythmias, and sudden death, even at recommended dosages.', 'DDInter', 'Caution and close monitoring is recommended if halofantrine is prescribed with CYP450 3A4 inhibitors, particularly potent ones like itraconazole, ketoconazole, posaconazole, voriconazole, clarithromycin, telithromycin, conivaptan, idelalisib, nefazodone, cobicistat, delavirdine, and most protease inhibitors. The manufacturer recommends performing an ECG before initiating halofantrine therapy and cardiac monitoring during and for 8 to 12 hours after completion of therapy.', 'Metabolism', 'Caution and close monitoring is recommended if halofantrine is prescribed with CYP450 3A4 inhibitors, particularly potent ones like itraconazole, ketoconazole, posaconazole, voriconazole, clarithromycin, telithromycin, conivaptan, idelalisib, nefazodone, cobicistat, delavirdine, and most protease inhibitors.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/143207/', '[1] Charbit B, Becquemont L, Lepere B, Peytavin G, Funck-Bretano C "Pharmacokinetic and pharmacodynamic interaction between grapefruit juice and halofantrine." Clin Pharmacol Ther 72 (2002): 514-23[2] Giao PT, de Vries PJ "Pharmacokinetic interactions of antimalarial agents." Clin Pharmacokinet 40 (2001): 343-73[3] "Product Information. Halfan (halofantrine)." GlaxoSmithKline, Philadelphia, PA.[4] Abernethy DR, Wesche DL, Barbey JT, et al. "Stereoselective halofantrine disposition and effect: concentration-related QTc prolongation." Br J Clin Pharmacol 51 (2001): 231-7[5] Baune B, Flinois JP, Furlan V, et al. "Halofantrine metabolism in microsomes in man: major role of CYP 3A4 and CYP 3A5." J Pharm Pharmacol 51 (1999): 419-26[6] Giao PT, de Vries PJ "Pharmacokinetic interactions of antimalarial agents." Clin Pharmacokinet 40 (2001): 343-73[7] "Product Information. Halfan (halofantrine)." GlaxoSmithKline, Philadelphia, PA.[8] Abernethy DR, Wesche DL, Barbey JT, et al. "Stereoselective halofantrine disposition and effect: concentration-related QTc prolongation." Br J Clin Pharmacol 51 (2001): 231-7[9] Charbit B, Becquemont L, Lepere B, Peytavin G, Funck-Bretano C "Pharmacokinetic and pharmacodynamic interaction between grapefruit juice and halofantrine." Clin Pharmacol Ther 72 (2002): 514-23[10] "Product Information. Zokinvy (lonafarnib)." Eiger BioPharmaceuticals, Palo Alto, CA.', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Dolutegravir, More', 'Artesunate, Mefloquine, Lumefantrine, Pyrimethamine, Tafenoquine, Atovaquone, Primaquine, Proguanil, Sulfadoxine', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (143045, 'Haloperidol', 'Nelfinavir', 'Moderate', 'Coadministration with nelfinavir may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by nelfinavir.', 'DDInter', 'Caution is advised if nelfinavir must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever nelfinavir is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if nelfinavir must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/143208/', '[1] "Product Information. Viracept (nelfinavir)." Agouron Pharma Inc, La Jolla, CA.', 'Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Amprenavir, Simeprevir, Valaciclovir, Zanamivir, Cabotegravir, Telaprevir, Velpatasvir, More', 'Chlorpromazine, Mesoridazine, Methotrimeprazine, Prochlorperazine, Fluphenazine, Promazine, Asenapine, Thiothixene, Thioridazine, Clozapine, Perphenazine, More', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (143046, 'Hydroxychloroquine', 'Nelfinavir', 'Moderate', 'Coadministration with moderate or strong inhibitors of CYP450 2C8 or CYP450 3A4 may increase the plasma concentration of hydroxychloroquine and the risk of toxicities such as QT interval prolongation and ventricular arrhythmias.', 'DDInter', 'Caution and close monitoring is recommended whenever hydroxychloroquine or chloroquine is used concomitantly with a moderate or strong CYP450 3A4 or 2C8 inhibitor. Clinical and laboratory monitoring should be considered whenever a moderate or strong CYP450 3A4 or 2C8 inhibitor is added to or withdrawn from therapy with hydroxychloroquine or chloroquine, and the dosage adjusted as necessary. Patients should be advised to seek medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitations, irregular heartbeat, shortness of breath, or syncope.', 'Metabolism', 'Caution and close monitoring is recommended whenever hydroxychloroquine or chloroquine is used concomitantly with a moderate or strong CYP450 3A4 or 2C8 inhibitor.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/143209/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] "Product Information. Zokinvy (lonafarnib)." Eiger BioPharmaceuticals, Palo Alto, CA.[3] Cerner Multum, Inc. "Australian Product Information." O 0', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Zanamivir, Lenacapavir, Velpatasvir, Dolutegravir, More', 'Artesunate, Mefloquine, Lumefantrine, Pyrimethamine, Tafenoquine, Atovaquone, Primaquine, Proguanil, Sulfadoxine', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (143047, 'Ibrutinib', 'Nelfinavir', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of ibrutinib, which is primarily metabolized by the isoenzyme.', 'DDInter', 'The use of ibrutinib in combination with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, posaconazole, voriconazole, conivaptan, nefazodone, cobicistat, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics should generally be avoided, particularly those that are intended for chronic administration. Some authorities recommend avoiding concomitant use of ibrutinib during and for 2 weeks after treatment with itraconazole. Alternative agents with no or minimal CYP450 3A4 inhibitory potential are recommended whenever possible. If no alternatives exist and the CYP450 3A4 inhibitor is used short-term for 7 days or less, consider interrupting or delaying initiation of ibrutinib treatment until therapy with the inhibitor is complete. Patients should be closely monitored for signs of ibrutinib toxicity such as myelosuppression, bleeding, infection, and renal impairment.', 'Metabolism', 'The use of ibrutinib in combination with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, posaconazole, voriconazole, conivaptan, nefazodone, cobicistat, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics should generally be avoided, particularly those that are intended for chronic administration.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/143210/', '[1] "Product Information. Sporanox (itraconazole)." Janssen Pharmaceutica, Titusville, NJ.[2] "Product Information. Imbruvica (ibrutinib)." Pharmacyclics Inc, Sunnyvale, CA.[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[5] "Product Information. Imbruvica (ibrutinib)." Pharmacyclics Inc, Sunnyvale, CA.[6] "Product Information. Zokinvy (lonafarnib)." Eiger BioPharmaceuticals, Palo Alto, CA.', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Rilpivirine, Dolutegravir, More', 'Alpelisib, Lenvatinib, Trametinib, Vandetanib, Alectinib, Dacomitinib, Tivozanib, Sorafenib', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (143048, 'Idarubicin', 'Nelfinavir', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein (P-gp) may increase the plasma concentrations of daunorubicin and idarubicin, both of which are substrates of the efflux transporter also known as ABCB1 or MDR1. The interaction has been studied with cyclosporine, a potent P-gp inhibitor, in attempt to overcome multidrug resistance in MDR1-overexpressing tumors.', 'DDInter', 'Caution is advised if daunorubicin or idarubicin is prescribed in combination with a P-gp inhibitor. Patients should be closely monitored for increased adverse effects including cardiotoxicity and myelosuppression.', 'Absorption', 'Caution is advised if daunorubicin or idarubicin is prescribed in combination with a P-gp inhibitor.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/143211/', '[1] "Product Information. Daunoxome (daunorubicin liposomal)." Nexstar Pharmaceuticals Inc, Boulder, CO.[2] "Product Information. Idamycin (idarubicin)." Pharmacia and Upjohn, Kalamazoo, MI.[3] "Multum Information Services, Inc. Expert Review Panel"[4] "Product Information. Cerubidine (daunorubicin)." Wyeth-Ayerst Laboratories, Philadelphia, PA.[5] "Product Information. Incivek (telaprevir)." Vertex Pharmaceuticals, Cambridge, MA.', 'Lamivudine, Paritaprevir, Tenofovir alafenamide, Maraviroc, Amprenavir, Simeprevir, Valaciclovir, Zanamivir, Cabotegravir, Etravirine, Dolutegravir, More', 'Valrubicin, Plicamycin, Bleomycin, Mitomycin, Dactinomycin, Epirubicin', 1767369485);
