INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (143366, 'Aluminum hydroxide', 'Neratinib', 'Moderate', 'Coadministration with drugs that increase gastric pH may significantly decrease the oral bioavailability of neratinib. The solubility of neratinib is pH-dependent and increases as neratinib becomes protonated at acidic pH, thus an increase in pH may interfere with its absorption.', 'DDInter', 'Concomitant use of neratinib with drugs that increase gastric pH should generally be avoided. If acid-suppression therapy is required, short-acting antacids should be considered, with neratinib dosing separated by at least three hours after antacid administration.', 'Absorption', 'Concomitant use of neratinib with drugs that increase gastric pH should generally be avoided.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/143529/', '[1] "Product Information. Nerlynx (neratinib)." Puma Biotechnology, Inc., Los Angeles, CA.[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Abbas R, Hug BA, Leister C, Burns J, Sonnichsen D "Pharmacokinetics of oral neratinib during co-administration of ketoconazole in healthy subjects." Br J Clin Pharmacol 71 (2011): 522-7[4] "Product Information. Nerlynx (neratinib)." Puma Biotechnology, Inc., Los Angeles, CA.', 'Entrectinib, Imatinib, Pralsetinib, Asciminib, Dacomitinib, Pacritinib, Tepotinib, Fedratinib, Avapritinib, Larotrectinib, Vemurafenib, More', NULL, 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (143367, 'Amprenavir', 'Neratinib', 'Major', 'Coadministration with potent or moderate inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of neratinib, which is primarily metabolized by the isoenzyme.', 'DDInter', 'Given the potential for increased risk of toxicity, concomitant use of neratinib with potent or moderate inhibitors of CYP450 3A4 should generally be avoided.', 'Metabolism', 'Given the potential for increased risk of toxicity, concomitant use of neratinib with potent or moderate inhibitors of CYP450 3A4 should generally be avoided.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/143530/', '[1] "Product Information. Nerlynx (neratinib)." Puma Biotechnology, Inc., Los Angeles, CA.[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Abbas R, Hug BA, Leister C, Burns J, Sonnichsen D "Pharmacokinetics of oral neratinib during co-administration of ketoconazole in healthy subjects." Br J Clin Pharmacol 71 (2011): 522-7[4] Cerner Multum, Inc. "Australian Product Information." O 0[5] "Product Information. Nerlynx (neratinib)." Puma Biotechnology, Inc., Los Angeles, CA.[6] Abbas R, Hug BA, Leister C, Burns J, Sonnichsen D "Pharmacokinetics of oral neratinib during co-administration of ketoconazole in healthy subjects." Br J Clin Pharmacol 71 (2011): 522-7[7] "Product Information. Zokinvy (lonafarnib)." Eiger BioPharmaceuticals, Palo Alto, CA.', 'Lamivudine, Glecaprevir, Tenofovir alafenamide, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Rilpivirine, Dolutegravir, Oseltamivir, More', 'Futibatinib, Tucatinib, Alpelisib, Lenvatinib, Trametinib, Nintedanib, Vandetanib, Alectinib, Dacomitinib, Tivozanib, Sorafenib', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (143368, 'Neratinib', 'Artesunate', 'Moderate', 'Coadministration with neratinib may increase the plasma concentrations and the risk of adverse effects of drugs that are substrates of the breast cancer resistance protein (BCRP) transporter, such as dabigatran, rosuvastatin, sulfasazine, and topotecan. The proposed mechanisms, based on in vitro data, is decreased clearance due to neratinib-mediated inhibition of BCRP transport protein. There are no clinical data regarding the use of neratinib with BCRP substrates.', 'DDInter', 'Caution is advised if neratinib is used concomitantly with drugs that are substrates of BCRP transport protein, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever neratinib is added to or withdrawn from therapy with these drugs. Patients should be monitored for the development of adverse effects.', 'Others', 'Caution is advised if neratinib is used concomitantly with drugs that are substrates of BCRP transport protein, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/143531/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Abbas R, Hug BA, Leister C, Burns J, Sonnichsen D "Pharmacokinetics of oral neratinib during co-administration of ketoconazole in healthy subjects." Br J Clin Pharmacol 71 (2011): 522-7[4] "Product Information. Nerlynx (neratinib)." Puma Biotechnology, Inc., Los Angeles, CA.', 'Mefloquine, Lumefantrine, Artemether, Pyrimethamine, Tafenoquine, Atovaquone, Hydroxychloroquine, Halofantrine, Quinine, Primaquine, Pro