INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (152003, 'Hydroxychloroquine', 'Penicillamine', 'Moderate', 'The concomitant use of penicillamine and chloroquine or hydroxychloroquine may potentiate the risk of hematologic and dermatologic adverse reactions due to overlapping toxicity profiles of these drugs. In addition, coadministration with chloroquine has been shown to increase the plasma concentrations of penicillamine.', 'DDInter', 'In general, penicillamine and chloroquine or hydroxychloroquine therapy should not be administered concurrently. Caution is advised if these drugs must be used together. Pharmacologic response to penicillamine should be monitored more closely whenever chloroquine is added to or withdrawn from therapy, and the penicillamine dosage adjusted as necessary. Patients should be advised to notify their physician if they experience signs and symptoms of toxicity such as fever, chills, sore throat, unusual bruising or bleeding, hematuria, coughing, wheezing, unexplained shortness of breath, muscle weakness, or visual disturbances.', 'Synergism', 'In general, penicillamine and chloroquine or hydroxychloroquine therapy should not be administered concurrently.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/152166/', '[1] "Product Information. Cuprimine (penicillamine)." Merck & Co, Inc, West Point, PA.[2] Seideman P, Linstrom B "Pharmacokinetic interactions of pencillamine in rheumatoid arthritis." J Rheumatol 16 (1989): 473-4[3] Haagsma CJ "Clinically important drug interactions with disease-modifying antirheumatic drugs." Drugs Aging 13 (1998): 281-9[4] Osman MA, Patel RB, Schuna A, Sundstrom WR, Welling PG "Reduction in oral penicillamine absorption by food, antacid and ferrous sulfate." Clin Pharmacol Ther 33 (1983): 465-70[5] "Product Information. Cuprimine (penicillamine)." Merck & Co, Inc, West Point, PA.[6] Cerner Multum, Inc. "Australian Product Information." O 0', 'Artesunate, Mefloquine, Lumefantrine, Artemether, Pyrimethamine, Tafenoquine, Atovaquone, Halofantrine, Quinine, Primaquine, Proguanil, Sulfadoxine', NULL, 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (152004, 'Iodipamide', 'Penicillamine', 'Major', 'Concomitant use of intravascular radiocontrast media with other nephrotoxic agents may potentiate the risk of contrast-induced nephropathy and renal impairment. While the condition is usually transient and asymptomatic, it can be associated with increased risk of renal failure, dialysis, prolonged hospitalization, significant long-term morbidity, and mortality.', 'DDInter', 'Alternative imaging techniques that do not require contrast should be considered in patients who are at increased risk for contrast-induced nephropathy. Any repeat procedures with contrast media, if necessary, should not occur until at least 48 to 72 hours after the previous contrast exposure and renal function has fully recovered.', 'Synergism', 'Alternative imaging techniques that do not require contrast should be considered in patients who are at increased risk for contrast-induced nephropathy.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/152167/', '[1] Dubrovskaya Y, Prasad N, Lee Y, Esaian D, Figueroa DA, Tam VH "Risk factors for nephrotoxicity onset associated with polymyxin B therapy." J Antimicrob Chemother 70 (2015): 1903-7[2] Bentley ML, Corwin HL, Dasta J "Drug-induced acute kidney injury in the critically ill adult: recognition and prevention strategies." Crit Care Med 38(6 Suppl) (2010): S169-74[3] Kellum JA, Leblanc M, Venkataraman R "Acute renal failure." BMJ Clin Evid 9 (2008): 2001[4] van den Berk G, Tonino S, de Fijter C, Smit W, Schultz MJ "Bench-to-bedside review: Preventative measures for contrast-induced nephropathy in critically ill patients." Crit Care 9 (2005): 361-70[5] Marcos LA, Camins BC, Ritchie DJ, Casabar E, Warren DK "Acute