INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (152096, 'Hemin', 'Pentobarbital', 'Major', 'Coadministration with drugs that are capable of increasing the activity of delta-aminolevulinic acid synthetase (e.g., estrogens, barbiturates, steroid metabolites, rifampin) may negate the pharmacologic effect of hemin. In the treatment of porphyria, hemin is thought to limit the rate of porphyrin/heme biosynthesis by inhibiting delta-aminolevulinic acid synthetase, thus any induction of this enzyme would be expected to counteract the therapeutic effect of hemin.', 'DDInter', 'Drugs capable of increasing the activity of delta-aminolevulinic acid synthetase should be avoided during hemin therapy.', 'Antagonism', 'Drugs capable of increasing the activity of delta-aminolevulinic acid synthetase should be avoided during hemin therapy.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/152259/', '[1] "Product Information. Panhematin (hemin)." Recordati Rare Diseases Inc, Lebanon, NJ.', 'Suvorexant, Triazolam, Flurazepam, Zolpidem, Zaleplon, Dichloralphenazone, Valerian, Temazepam, Chloral hydrate, Quazepam, Paraldehyde, More', 'Voxelotor, Conestat alfa, Betibeglogene autotemcel, Hyaluronidase, Streptokinase, Human C1-esterase inhibitor, Berotralstat, Lanadelumab, Icatibant, Crizanlizumab', 1767369485);
