INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (152980, 'Halofantrine', 'Pexidartinib', 'Moderate', 'Coadministration with pexidartinib may decrease the plasma concentrations and therapeutic effects of drugs that are substrates of CYP450 3A4. The proposed mechanism is increased clearance due to pexidartinib-mediated induction of CYP450 3A4 isoenzyme.', 'DDInter', 'Concomitant administration of pexidartinib with drugs that are substrates of CYP450 3A4 should generally be avoided, particularly those with a narrow therapeutic range. The potential for diminished therapeutic effects should be considered when pexidartinib is prescribed in combination with drugs that are substrates of CYP450 3A4. Dosage adjustments as well as clinical and laboratory monitoring of the CYP450 3A4 substrate drug should be considered whenever pexidartinib is added to or withdrawn from therapy with these drugs.', 'Metabolism', 'Concomitant administration of pexidartinib with drugs that are substrates of CYP450 3A4 should generally be avoided, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/153143/', '[1] "Product Information. Turalio (pexidartinib)." Daiichi Sankyo, Inc., Parsippany, NJ.[2] "Product Information. Turalio (pexidartinib)." Daiichi Sankyo, Inc., Parsippany, NJ.[3] "Product Information. Sonata (zaleplon)" Wyeth-Ayerst Laboratories, Philadelphia, PA.', 'Artesunate, Pyrimethamine, Tafenoquine, Hydroxychloroquine, Primaquine, Proguanil, Chloroquine', 'Acalabrutinib, Dacomitinib, Tepotinib, Avapritinib, Selumetinib, Cobimetinib, Capmatinib, Ibrutinib, Afatinib, Tivozanib, Zanubrutinib, More', 1767369485);
