INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (155798, 'Rolapitant', 'Posaconazole', 'Moderate', 'Coadministration with rolapitant may increase the plasma concentrations of drugs that are substrates of the P-glycoprotein (P-gp) efflux transporter.', 'DDInter', 'Caution is advised when rolapitant is prescribed with drugs that are P-gp substrates, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever rolapitant is added to or withdrawn from therapy.', 'Absorption', 'Caution is advised when rolapitant is prescribed with drugs that are P-gp substrates, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/155967/', '[1] "Product Information. Varubi (rolapitant)." Tesaro Inc., Waltham, MA.', 'Fluconazole, Caspofungin, Amphotericin B, Itraconazole, Miconazole, Micafungin, Oteseconazole, Ketoconazole, Flucytosine, Anidulafungin, Voriconazole', 'Scopolamine, Nabilone, Dronabinol', 1767369485);
