INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (159503, 'Clotrimazole', 'Ranolazine', 'Major', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of ranolazine, which is primarily metabolized by the isoenzyme. Because ranolazine prolongs QT interval in a dose-dependent manner, high plasma levels of ranolazine may increase the risk of ventricular arrhythmias such as ventricular tachycardia, ventricular fibrillation, and torsade de pointes.', 'DDInter', 'The dosage of ranolazine should not exceed 500 mg twice a day when coadministered with moderate inhibitors of CYP450 3A4, including but not limited to diltiazem, verapamil, aprepitant, erythromycin, fluconazole, and grapefruit juice. Patients should be advised to seek medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, palpitations, or syncope.', 'Metabolism', 'The dosage of ranolazine should not exceed 500 mg twice a day when coadministered with moderate inhibitors of CYP450 3A4, including but not limited to diltiazem, verapamil, aprepitant, erythromycin, fluconazole, and grapefruit juice.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/159676/', '[1] "Product Information. Ranexa (ranolazine)." Calmoseptine Inc, Huntington Beach, CA.[2] European Medicines Agency "CHMP Assessment Report for Latixa. International nonproprietary name: ranolazine. Procedure No. EMEA/H/C/805. Available from: URL: http://www.emea.europa.eu/humandocs/PDFs/EPAR/latixa/H-805-en6.pdf." ([cited 2008]):[3] "Product Information. Ranexa (ranolazine)." Calmoseptine Inc, Huntington Beach, CA.[4] "Product Information. Ranexa (ranolazine)." Calmoseptine Inc, Huntington Beach, CA.', 'Adenosine, Regadenoson, Alprostadil, Ubidecarenone', 'Salicylic acid, Selenium Sulfide, Thiabendazole, Nystatin, Ciclopirox, Econazole, Flucytosine, Tioconazole, Chlorphenesin, Tetracycline, Minocycline, More', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (159504, 'Copanlisib', 'Ranolazine', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 and P-glycoprotein (P-gp) may increase the plasma concentrations of copanlisib, which is a substrate of both the isoenzyme and efflux transporter.', 'DDInter', 'Caution is advised when copanlisib is used with CYP450 3A4 inhibitors. Patients should be monitored for potentially increased adverse effects such as nausea, vomiting, diarrhea, stomatitis, hyperglycemia, hypertension, noninfectious pneumonitis, cutaneous reactions (e.g., exfoliative dermatitis, maculopapular rash), anemia, neutropenia, thrombocytopenia, and infections. Dosage adjustments for copanlisib may be necessary and should be done in accordance with the product labeling.', 'Metabolism', 'Caution is advised when copanlisib is used with CYP450 3A4 inhibitors.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/159677/', '[1] "Product Information. Aliqopa (copanlisib)." Bayer Pharmaceutical Inc, West Haven, CT.[2] "Product Information. Aliqopa (copanlisib)." Bayer Pharmaceutical Inc, West Haven, CT.', 'Alprostadil, Indomethacin, Ibuprofen, Ubidecarenone, Ivabradine, Adenosine, Regadenoson', 'Regorafenib, Temsirolimus, Trametinib, Erlotinib, Neratinib, Tivozanib, Alectinib', 1767369485);
