INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (159541, 'Galantamine', 'Ranolazine', 'Moderate', 'Coadministration with ranolazine may increase the plasma concentrations of drugs that are substrates of the CYP450 2D6 isoenzyme. Ranolazine has been shown in vitro to be an inhibitor of CYP450 2D6. However, concomitant use of ranolazine with other drugs that are metabolized by CYP450 2D6 such as tricyclic antidepressants and antipsychotics has not been studied.', 'DDInter', 'Caution is advised if ranolazine must be used concurrently with medications that undergo metabolism by CYP450 2D6, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever ranolazine is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if ranolazine must be used concurrently with medications that undergo metabolism by CYP450 2D6, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/159714/', '[1] "Product Information. Ranexa (ranolazine)." Calmoseptine Inc, Huntington Beach, CA.[2] "Product Information. Ranexa (ranolazine)." Calmoseptine Inc, Huntington Beach, CA.', 'Alprostadil, Indomethacin, Ibuprofen, Ubidecarenone, Adenosine, Regadenoson', 'Memantine, Ginkgo biloba, Aducanumab', 1767369485);
