INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (162003, 'Voxelotor', 'Rivaroxaban', 'Moderate', 'Coadministration with moderate or potent inhibitors of CYP450 3A4 may increase the plasma concentrations of rivaroxaban, which is a substrate of the isoenzyme.', 'DDInter', 'In patients with significant renal impairment, the use of rivaroxaban with moderate or potent inhibitors of CYP450 3A4 should be approached with caution. Patients should be routinely evaluated for signs and symptoms suggesting blood loss such as a drop in hemoglobin and/or hematocrit, hypotension, or fetal distress (in pregnant women). Renal function should also be assessed periodically, and treatment with rivaroxaban discontinued if acute renal failure develops. Due to the lack of clinical data, rivaroxaban is not recommended in patients with CrCl below 30 mL/min when used for the prophylaxis of deep vein thrombosis and in patients with CrCl below 15 mL/min when used for reducing the risk of stroke and systemic embolism in nonvalvular atrial fibrillation.', 'Metabolism', 'In patients with significant renal impairment, the use of rivaroxaban with moderate or potent inhibitors of CYP450 3A4 should be approached with caution.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/162176/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Xarelto (rivaroxaban)." Bayer Inc, Toronto, IA.', 'Betrixaban, Clopidogrel, Iloprost, Lepirudin, Acenocoumarol, Urokinase, Cangrelor, Dipyridamole, Caplacizumab, Argatroban, Prasugrel, More', 'Betibeglogene autotemcel, Hyaluronidase, Lanadelumab, Human C1-esterase inhibitor, Crizanlizumab, Conestat alfa, Icatibant', 1767369485);
